import React from 'react';
import { BrowserRouter as Router, Routes, Route, Navigate } from 'react-router-dom';
import { AuthProvider } from './contexts/AuthContext';
import { ProtectedRoute, AdminRoute } from './components/ProtectedRoute';
import App from './App';
import AdminPage from './pages/AdminPage';
import { AuthPage } from './pages/AuthPage';
import { SetPasswordPage } from './pages/SetPasswordPage';

// ============================================
// CONFIGURAÃ‡ÃƒO DE ROTAS
// ============================================

const AppRouter = () => {
  console.log('ðŸ“± AppRouter renderizando');
  return (
    <Router future={{ v7_startTransition: true, v7_relativeSplatPath: true }}>
      <AuthProvider>
        <Routes>
          {/* Rota de login/registro */}
          <Route path="/auth" element={<AuthPage />} />
          <Route path="/login" element={<AuthPage />} />
          <Route path="/register" element={<AuthPage />} />
          <Route path="/set-password" element={<SetPasswordPage />} />

          {/* Rota protegida - Dashboard principal */}
          <Route
            path="/dashboard"
            element={
              <ProtectedRoute>
                <App />
              </ProtectedRoute>
            }
          />

          {/* Rota protegida - Painel de admin (apenas para admins) */}
          <Route
            path="/admin"
            element={
              <AdminRoute>
                <AdminPage />
              </AdminRoute>
            }
          />

          {/* Rota raiz - redireciona para dashboard ou login */}
          <Route
            path="/"
            element={<Navigate to="/dashboard" />}
          />

          {/* Rota 404 */}
          <Route
            path="*"
            element={<Navigate to="/dashboard" />}
          />
        </Routes>
      </AuthProvider>
    </Router>
  );
};

export default AppRouter;
