import React from 'react';
import { Link } from 'react-router-dom';
import { useAuth } from '../contexts/AuthContext';
import AdminPanel from '../components/AdminPanel';
import ProfileMenu from '../components/ProfileMenu';
import './AdminPage.css';

// ============================================
// P√ÅGINA DE ADMINISTRA√á√ÉO
// Apenas admins podem acessar
// ============================================

const AdminPage = () => {
  const { user, loading, isAdmin } = useAuth();

  // Carregando
  if (loading) {
    return (
      <div className="admin-page">
        <div className="loading">
          <div className="spinner"></div>
          <p>Carregando...</p>
        </div>
      </div>
    );
  }

  // N√£o autenticado
  if (!user) {
    return (
      <div className="admin-page">
        <div className="access-denied">
          <h1>üîê Acesso Negado</h1>
          <p>Voc√™ precisa estar autenticado para acessar esta p√°gina.</p>
          <a href="/login" className="btn btn-primary">
            Ir para Login
          </a>
        </div>
      </div>
    );
  }

  // N√£o √© admin
  if (!isAdmin) {
    return (
      <div className="admin-page">
        <div className="access-denied">
          <h1>üö´ Sem Permiss√£o</h1>
          <p>Apenas administradores podem acessar o painel de administra√ß√£o.</p>
          <a href="/dashboard" className="btn btn-secondary">
            Voltar ao Dashboard
          </a>
        </div>
      </div>
    );
  }

  // Admin pode acessar
  return (
    <div className="admin-page">
      <div className="admin-container">
        <div className="admin-topbar">
          <Link to="/dashboard" className="admin-logo" aria-label="Voltar ao in√≠cio">
            <img
              src="/logo-proteq.png"
              alt="PROTEQ Logo"
              className="admin-logo-image"
              onError={(e) => (e.target.style.display = 'none')}
            />
          </Link>
          <ProfileMenu />
        </div>
        <AdminPanel />
      </div>
    </div>
  );
};

export default AdminPage;
