const formatarCNPJ = (valor) => {
  // Remove tudo que n√£o √© n√∫mero
  const numeros = valor.replace(/\D/g, '');
  
  // Aplica a m√°scara XX.XXX.XXX/XXXX-XX
  if (numeros.length <= 2) return numeros;
  if (numeros.length <= 5) return numeros.replace(/(\d{2})(\d{0,3})/, '$1.$2');
  if (numeros.length <= 8) return numeros.replace(/(\d{2})(\d{3})(\d{0,3})/, '$1.$2.$3');
  if (numeros.length <= 12) return numeros.replace(/(\d{2})(\d{3})(\d{3})(\d{0,4})/, '$1.$2.$3/$4');
  return numeros.replace(/(\d{2})(\d{3})(\d{3})(\d{4})(\d{0,2})/, '$1.$2.$3/$4-$5');
};

const formatarMoedaInput = (valor) => {
  // Remove tudo que n√£o √© n√∫mero
  const numeros = valor.replace(/\D/g, '');
  
  // Converte para n√∫mero e divide por 100
  const numero = parseFloat(numeros) / 100;
  
  // Formata como moeda brasileira
  return numero.toLocaleString('pt-BR', {
    minimumFractionDigits: 2,
    maximumFractionDigits: 2
  });
};
const formatarValorParaEmail = (valor) => {
  return valor.toLocaleString('pt-BR', {
    style: 'currency',
    currency: 'BRL'
  });
};
const parseMoedaParaNumero = (valor) => {
  // Se j√° for n√∫mero, retorna direto
  if (typeof valor === 'number') return valor;

  // Se for string vazia ou undefined, retorna 0
  if (!valor) return 0;

  // Remove pontos e substitui v√≠rgula por ponto
  return parseFloat(valor.toString().replace(/\./g, '').replace(',', '.')) || 0;
};

  const applyPipefyImport = (cards, fieldMap, pipeFields = []) => {
    if (!Array.isArray(cards) || cards.length === 0) return;

    const fieldLookup = Array.isArray(pipeFields) ? pipeFields : [];
    const normalize = (value) => (value || '').toString().trim();
  const getFieldValue = (fields, fieldIdOrName) => {
    if (!fieldIdOrName) return '';
    const fromPipe = fieldLookup.find((f) => f.id === fieldIdOrName);
    const candidates = [
      fieldIdOrName,
      fromPipe?.label,
      fromPipe?.id
    ]
      .filter(Boolean)
      .map((v) => String(v).toLowerCase());

    const found = fields.find((f) => candidates.includes(String(f.name || '').toLowerCase()));
    return found?.value || '';
  };

  const imported = cards.map((card) => {
    const fields = Array.isArray(card.fields) ? card.fields : [];
    const nomeEmpresa = normalize(getFieldValue(fields, fieldMap.nomeEmpresa)) || card.title || '';
    const nomeResponsavel = normalize(getFieldValue(fields, fieldMap.nomeResponsavel));
    const email = normalize(getFieldValue(fields, fieldMap.email));
    const telefone = normalize(getFieldValue(fields, fieldMap.telefone));
    const cnpj = normalize(getFieldValue(fields, fieldMap.cnpj));
    const codigoContrato = normalize(getFieldValue(fields, fieldMap.codigoContrato));
    const valorTotal = parseMoedaParaNumero(getFieldValue(fields, fieldMap.valorTotal));
    const parcelas = parseInt(getFieldValue(fields, fieldMap.parcelas), 10) || 1;
    const dataVenda = normalize(getFieldValue(fields, fieldMap.dataVenda));
    const proximoVencimento = normalize(getFieldValue(fields, fieldMap.proximoVencimento));
    const linkPagamento = normalize(getFieldValue(fields, fieldMap.linkPagamento));
    const observacoes = normalize(getFieldValue(fields, fieldMap.observacoes));

    return {
      id: card.id,
      nomeResponsavel,
      nomeEmpresa,
      email,
      telefone,
      valorTotal,
      parcelas,
      dataVenda,
      proximoVencimento,
      cnpj,
      codigoContrato,
      linkPagamento,
      observacoes,
      valorPago: 0,
      parcelasPagas: 0,
      historicosPagamentos: []
    };
  });

  setClientes((prev) => {
    const updated = [...prev];
    imported.forEach((cliente) => {
      const matchIndex = updated.findIndex((c) => {
        if (cliente.email && c.email === cliente.email) return true;
        if (cliente.cnpj && c.cnpj === cliente.cnpj) return true;
        if (cliente.codigoContrato && c.codigoContrato === cliente.codigoContrato) return true;
        return false;
      });

      if (matchIndex >= 0) {
        updated[matchIndex] = { ...updated[matchIndex], ...cliente };
      } else {
        updated.push(cliente);
      }
    });
    localStorage.setItem('financial-manager-clientes', JSON.stringify(updated));
    return updated;
  });
};
