      'Status': calcularStatus(cliente) === 'pago' ? 'Pago' : 
               calcularStatus(cliente) === 'em_atraso' ? 'Em Atraso' : 'Pendente',
      'Data da Venda': cliente.dataVenda || 'N√£o informado',
