import React, { useEffect, useState } from 'react';
import { CheckCircle, AlertCircle } from 'lucide-react';

const STORAGE_KEY = 'pipefy-integration-settings';

const PipefyIntegration = () => {
  const [config, setConfig] = useState({
    apiToken: '',
    organizationId: '',
    pipeId: '',
    webhookUrl: ''
  });
  const [savedAt, setSavedAt] = useState(null);

  useEffect(() => {
    const saved = localStorage.getItem(STORAGE_KEY);
    if (saved) {
      try {
        const parsed = JSON.parse(saved);
        setConfig({
          apiToken: parsed.apiToken || '',
          organizationId: parsed.organizationId || '',
          pipeId: parsed.pipeId || '',
          webhookUrl: parsed.webhookUrl || ''
        });
        setSavedAt(parsed.savedAt || null);
      } catch {
        // Ignore invalid storage
      }
    }
  }, []);

  const handleSave = () => {
    const payload = { ...config, savedAt: new Date().toISOString() };
    localStorage.setItem(STORAGE_KEY, JSON.stringify(payload));
    setSavedAt(payload.savedAt);
  };

  const handleClear = () => {
    localStorage.removeItem(STORAGE_KEY);
    setConfig({
      apiToken: '',
      organizationId: '',
      pipeId: '',
      webhookUrl: ''
    });
    setSavedAt(null);
  };

  const isConfigured = Boolean(config.apiToken && config.organizationId && config.pipeId);

  return (
    <div>
      <div className="flex items-center justify-between mb-6">
        <div>
          <h3 className="text-xl font-bold text-slate-800">Integração Pipefy</h3>
          <p className="text-sm text-slate-600">Configure tokens e IDs para iniciar a integração.</p>
        </div>
        <div className={`flex items-center gap-2 px-3 py-2 rounded-full text-sm font-semibold ${
          isConfigured ? 'bg-green-100 text-green-800' : 'bg-yellow-100 text-yellow-800'
        }`}>
          {isConfigured ? <CheckCircle className="w-4 h-4" /> : <AlertCircle className="w-4 h-4" />}
          {isConfigured ? 'Configuração ok' : 'Configuração pendente'}
        </div>
      </div>

      <div className="grid grid-cols-1 xl:grid-cols-3 gap-6">
        <div className="xl:col-span-2 bg-white rounded-lg shadow-md p-6">
          <h4 className="font-semibold text-slate-800 mb-4">Credenciais e IDs</h4>
          <div className="grid grid-cols-1 md:grid-cols-2 gap-4">
            <div className="md:col-span-2">
              <label className="block text-sm font-medium text-gray-700 mb-1">API Token</label>
              <input
                type="password"
                className="w-full px-3 py-2 border border-gray-300 rounded-lg focus:ring-2 focus:ring-blue-500 text-gray-900"
                value={config.apiToken}
                onChange={(e) => setConfig({ ...config, apiToken: e.target.value })}
                placeholder="Cole o token de API do Pipefy"
              />
            </div>
            <div>
              <label className="block text-sm font-medium text-gray-700 mb-1">Organization ID</label>
              <input
                type="text"
                className="w-full px-3 py-2 border border-gray-300 rounded-lg focus:ring-2 focus:ring-blue-500 text-gray-900"
                value={config.organizationId}
                onChange={(e) => setConfig({ ...config, organizationId: e.target.value })}
                placeholder="Ex: 123456"
              />
            </div>
            <div>
              <label className="block text-sm font-medium text-gray-700 mb-1">Pipe ID</label>
              <input
                type="text"
                className="w-full px-3 py-2 border border-gray-300 rounded-lg focus:ring-2 focus:ring-blue-500 text-gray-900"
                value={config.pipeId}
                onChange={(e) => setConfig({ ...config, pipeId: e.target.value })}
                placeholder="Ex: 987654"
              />
            </div>
            <div className="md:col-span-2">
              <label className="block text-sm font-medium text-gray-700 mb-1">Webhook URL</label>
              <input
                type="text"
                className="w-full px-3 py-2 border border-gray-300 rounded-lg focus:ring-2 focus:ring-blue-500 text-gray-900"
                value={config.webhookUrl}
                onChange={(e) => setConfig({ ...config, webhookUrl: e.target.value })}
                placeholder="https://seu-dominio.com/webhooks/pipefy"
              />
            </div>
          </div>

          <div className="flex gap-4 mt-6">
            <button
              onClick={handleSave}
              className="px-6 py-3 bg-blue-600 text-white rounded-lg hover:bg-blue-700 transition-colors shadow-md"
            >
              Salvar Configuração
            </button>
            <button
              onClick={handleClear}
              className="px-6 py-3 border border-gray-300 text-gray-700 rounded-lg hover:bg-gray-50 transition-colors"
            >
              Limpar
            </button>
          </div>
          {savedAt && (
            <p className="text-xs text-slate-500 mt-3">
              Última atualização: {new Date(savedAt).toLocaleString('pt-BR')}
            </p>
          )}
        </div>

        <div className="bg-gradient-to-br from-slate-50 to-gray-50 rounded-lg shadow-md p-6 border border-slate-200/60">
          <h4 className="font-semibold text-slate-800 mb-3">Próximos passos</h4>
          <ul className="text-sm text-slate-600 space-y-2">
            <li>Conectar API do Pipefy ao backend.</li>
            <li>Sincronizar clientes e status financeiros.</li>
            <li>Mapear campos do Pipe para dados do sistema.</li>
            <li>Configurar webhooks para atualizações em tempo real.</li>
          </ul>
        </div>
      </div>
    </div>
  );
};

export default PipefyIntegration;
