import React, { useState, useEffect } from 'react';
import { TrendingUp, DollarSign, AlertCircle, Calendar, Clock, CheckCircle, BarChart3, Briefcase, CreditCard, ArrowUpRight, ArrowDownRight, Wallet, Edit2, Plus, Trash2, X, Save, Download } from 'lucide-react';

const DashboardFinanceiro = ({ clientes = [] }) => {
  const [periodo, setPeriodo] = useState('mes');
  const [modalDespesas, setModalDespesas] = useState(false);
  const [modalMeta, setModalMeta] = useState(false);
  
  // Despesas editÃ¡veis (salvos no localStorage)
  const [despesas, setDespesas] = useState(() => {
    const salvas = localStorage.getItem('dashboard-despesas');
    return salvas ? JSON.parse(salvas) : [
      { categoria: 'Folha de Pagamento', valor: 45000, tipo: 'fixa' },
      { categoria: 'Aluguel/Infraestrutura', valor: 8000, tipo: 'fixa' },
      { categoria: 'Equipamentos', valor: 5000, tipo: 'variavel' },
      { categoria: 'Marketing', valor: 3000, tipo: 'variavel' },
      { categoria: 'Impostos', valor: 12000, tipo: 'fixa' },
      { categoria: 'Outros', valor: 4000, tipo: 'variavel' }
    ];
  });

  // Meta mensal editÃ¡vel
  const [metaMensal, setMetaMensal] = useState(() => {
    const salva = localStorage.getItem('dashboard-meta-mensal');
    return salva ? parseFloat(salva) : 100000;
  });

  const [novaDespesa, setNovaDespesa] = useState({
    categoria: '',
    valor: '',
    tipo: 'fixa'
  });

  // Estado para ediÃ§Ã£o de despesa
  const [editandoDespesa, setEditandoDespesa] = useState(null);

  // FunÃ§Ãµes para gerenciar despesas
  const adicionarDespesa = () => {
    if (novaDespesa.categoria && novaDespesa.valor) {
      // Converte string de centavos para nÃºmero
      const valorNumerico = parseFloat(novaDespesa.valor) / 100;
      setDespesas([...despesas, { ...novaDespesa, valor: valorNumerico }]);
      setNovaDespesa({ categoria: '', valor: '', tipo: 'fixa' });
    }
  };

  const editarDespesa = (index) => {
    setEditandoDespesa(index);
    
    // Se o valor for 0, armazena como string "0"
    const valorEmCentavos = despesas[index].valor === 0 
      ? '0' 
      : Math.round(despesas[index].valor * 100).toString();
      
    setNovaDespesa({ 
      ...despesas[index], 
      valor: valorEmCentavos 
    });
  };

  const salvarEdicaoDespesa = () => {
    if (novaDespesa.categoria && novaDespesa.valor) {
      const valorNumerico = parseFloat(novaDespesa.valor) / 100;
      const novasDespesas = [...despesas];
      novasDespesas[editandoDespesa] = { 
        ...novaDespesa, 
        valor: valorNumerico 
      };
      setDespesas(novasDespesas);
      setEditandoDespesa(null);
      setNovaDespesa({ categoria: '', valor: '', tipo: 'fixa' });
    }
  };

  const removerDespesa = (index) => {
    setDespesas(despesas.filter((_, i) => i !== index));
  };

  const cancelarEdicao = () => {
    setEditandoDespesa(null);
    setNovaDespesa({ categoria: '', valor: '', tipo: 'fixa' });
  };

  // Salvar despesas no localStorage
  useEffect(() => {
    localStorage.setItem('dashboard-despesas', JSON.stringify(despesas));
  }, [despesas]);

  // Salvar meta no localStorage
  useEffect(() => {
    localStorage.setItem('dashboard-meta-mensal', metaMensal.toString());
  }, [metaMensal]);

  const [metricas, setMetricas] = useState({
    receitaRealizada: 0,
    receitaPrevista: 0,
    receitaPendente: 0,
    inadimplencia: 0,
    previsaoProximoMes: 0,
    despesasTotais: 0,
    lucroOperacional: 0,
    margemLucro: 0,
    fluxoCaixa: 0,
    clientesAtivos: 0,
    clientesInadimplentes: 0
  });

  useEffect(() => {
    if (clientes) {
      calcularMetricas();
    }
  }, [clientes, despesas, periodo]);

  const calcularMetricas = () => {
    // Garantir que clientes Ã© um array vÃ¡lido
    const clientesArray = Array.isArray(clientes) ? clientes : [];
    
    if (clientesArray.length === 0) {
      // Se nÃ£o hÃ¡ clientes, zera tudo
      setMetricas({
        receitaRealizada: 0,
        receitaPrevista: 0,
        receitaPendente: 0,
        inadimplencia: 0,
        previsaoProximoMes: 0,
        despesasTotais: despesas.reduce((sum, d) => sum + d.valor, 0),
        lucroOperacional: 0,
        margemLucro: 0,
        fluxoCaixa: 0,
        clientesAtivos: 0,
        clientesInadimplentes: 0
      });
      return;
    }

    const hoje = new Date();
    const mesAtual = hoje.getMonth();
    const anoAtual = hoje.getFullYear();

    // Receitas
    const receitaRealizada = clientesArray.reduce((sum, c) => sum + (c.valorPago || 0), 0);
    const receitaPrevista = clientesArray.reduce((sum, c) => sum + (c.valorTotal || 0), 0);
    const receitaPendente = receitaPrevista - receitaRealizada;

    // InadimplÃªncia
    const totalAtrasado = clientesArray
      .filter(c => {
        if (!c.proximoVencimento) return false;
        return new Date(c.proximoVencimento) < hoje && c.valorPago < c.valorTotal;
      })
      .reduce((sum, c) => sum + (c.valorTotal - c.valorPago), 0);

    const inadimplencia = receitaPrevista > 0 ? (totalAtrasado / receitaPrevista) * 100 : 0;
    const clientesInadimplentes = clientesArray.filter(c => {
      if (!c.proximoVencimento) return false;
      return new Date(c.proximoVencimento) < hoje && c.valorPago < c.valorTotal;
    }).length;

    // PrevisÃ£o prÃ³ximo mÃªs
    const proximoMes = new Date(anoAtual, mesAtual + 1, 1);
    const fimProximoMes = new Date(anoAtual, mesAtual + 2, 0);
    
    const previsaoProximoMes = clientesArray
      .filter(c => {
        if (!c.proximoVencimento || c.valorPago >= c.valorTotal) return false;
        const venc = new Date(c.proximoVencimento);
        return venc >= proximoMes && venc <= fimProximoMes;
      })
      .reduce((sum, c) => sum + (c.valorParcela || 0), 0);

    // Despesas
    const despesasTotais = despesas.reduce((sum, d) => sum + d.valor, 0);

    // Lucro e Margem
    const lucroOperacional = receitaRealizada - despesasTotais;
    const margemLucro = receitaRealizada > 0 ? (lucroOperacional / receitaRealizada) * 100 : 0;

    // Fluxo de Caixa
    const fluxoCaixa = lucroOperacional;

    // Clientes Ativos
    const clientesAtivos = clientesArray.filter(c => c.valorPago > 0 || (c.proximoVencimento && new Date(c.proximoVencimento) >= hoje)).length;

    setMetricas({
      receitaRealizada,
      receitaPrevista,
      receitaPendente,
      inadimplencia,
      previsaoProximoMes,
      despesasTotais,
      lucroOperacional,
      margemLucro,
      fluxoCaixa,
      clientesAtivos,
      clientesInadimplentes
    });
  };

  const formatMoney = (value) => {
    return new Intl.NumberFormat('pt-BR', {
      style: 'currency',
      currency: 'BRL'
    }).format(value);
  };

  // FunÃ§Ã£o para formataÃ§Ã£o de input que lida com zero
  const formatInputCurrency = (value) => {
    // Se for string vazia, retorna string vazia
    if (value === '') return '';
    
    // Converte para string e remove caracteres nÃ£o numÃ©ricos
    const apenasNumeros = value.toString().replace(/\D/g, '');
    
    // Se nÃ£o hÃ¡ nÃºmeros, retorna vazio
    if (apenasNumeros === '') return '';
    
    // Converte para nÃºmero em centavos
    const numero = parseFloat(apenasNumeros) / 100;
    
    // Se for NaN ou zero, mostra R$ 0,00
    if (isNaN(numero) || numero === 0) return 'R$ 0,00';
    
    // Formata normalmente
    return numero.toLocaleString('pt-BR', {
      style: 'currency',
      currency: 'BRL'
    });
  };

  // FunÃ§Ã£o para exportar dados do dashboard
  const exportarDashboard = () => {
    const dataAtual = new Date().toLocaleDateString('pt-BR');
    const periodoTexto = periodo === 'mes' ? 'Mensal' : periodo === 'trimestre' ? 'Trimestral' : 'Anual';
    
    // Preparar dados das mÃ©tricas
    const dadosMetricas = [
      ['RelatÃ³rio Financeiro', periodoTexto, dataAtual],
      [''],
      ['MÃ‰TRICAS PRINCIPAIS'],
      ['Receita Realizada', formatMoney(metricas.receitaRealizada)],
      ['Lucro Operacional', formatMoney(metricas.lucroOperacional)],
      ['Margem de Lucro', `${metricas.margemLucro.toFixed(2)}%`],
      ['Receita Pendente', formatMoney(metricas.receitaPendente)],
      ['Despesas Totais', formatMoney(metricas.despesasTotais)],
      [''],
      ['MÃ‰TRICAS SECUNDÃRIAS'],
      ['Taxa de InadimplÃªncia', `${metricas.inadimplencia.toFixed(2)}%`],
      ['Clientes Ativos', metricas.clientesAtivos.toString()],
      ['Clientes Inadimplentes', metricas.clientesInadimplentes.toString()],
      ['PrevisÃ£o PrÃ³ximo MÃªs', formatMoney(metricas.previsaoProximoMes)],
      ['Meta Mensal', formatMoney(metaMensal)],
      [''],
      ['DESPESAS DETALHADAS']
    ];

    // Adicionar cabeÃ§alhos das despesas
    dadosMetricas.push(['Categoria', 'Valor', 'Tipo']);
    
    // Adicionar cada despesa
    despesas.forEach(despesa => {
      dadosMetricas.push([
        despesa.categoria,
        formatMoney(despesa.valor),
        despesa.tipo === 'fixa' ? 'Fixa' : 'VariÃ¡vel'
      ]);
    });

    // Converter para CSV
    const csvContent = dadosMetricas
      .map(row => row.map(cell => `"${cell}"`).join(','))
      .join('\n');

    // Criar e baixar o arquivo CSV
    const blob = new Blob([csvContent], { type: 'text/csv;charset=utf-8;' });
    const link = document.createElement('a');
    const url = URL.createObjectURL(blob);
    link.setAttribute('href', url);
    link.setAttribute('download', `dashboard-financeiro-${dataAtual.replace(/\//g, '-')}.csv`);
    link.style.visibility = 'hidden';
    document.body.appendChild(link);
    link.click();
    document.body.removeChild(link);
  };

  // FunÃ§Ã£o para exportar para Google Sheets
  const exportarParaGoogleSheets = async () => {
    const dataAtual = new Date().toLocaleDateString('pt-BR');
    const periodoTexto = periodo === 'mes' ? 'Mensal' : periodo === 'trimestre' ? 'Trimestral' : 'Anual';
    
    // Preparar dados das mÃ©tricas
    const dadosMetricas = [
      ['RelatÃ³rio Financeiro', periodoTexto, dataAtual],
      [''],
      ['MÃ‰TRICAS PRINCIPAIS'],
      ['Receita Realizada', formatMoney(metricas.receitaRealizada)],
      ['Lucro Operacional', formatMoney(metricas.lucroOperacional)],
      ['Margem de Lucro', `${metricas.margemLucro.toFixed(2)}%`],
      ['Receita Pendente', formatMoney(metricas.receitaPendente)],
      ['Despesas Totais', formatMoney(metricas.despesasTotais)],
      [''],
      ['MÃ‰TRICAS SECUNDÃRIAS'],
      ['Taxa de InadimplÃªncia', `${metricas.inadimplencia.toFixed(2)}%`],
      ['Clientes Ativos', metricas.clientesAtivos.toString()],
      ['Clientes Inadimplentes', metricas.clientesInadimplentes.toString()],
      ['PrevisÃ£o PrÃ³ximo MÃªs', formatMoney(metricas.previsaoProximoMes)],
      ['Meta Mensal', formatMoney(metaMensal)],
      [''],
      ['DESPESAS DETALHADAS'],
      ['Categoria', 'Valor', 'Tipo']
    ];

    // Adicionar cada despesa
    despesas.forEach(despesa => {
      dadosMetricas.push([
        despesa.categoria,
        formatMoney(despesa.valor),
        despesa.tipo === 'fixa' ? 'Fixa' : 'VariÃ¡vel'
      ]);
    });

    // Converter para TSV (Tab Separated Values)
    const tsvContent = dadosMetricas
      .map(row => row.join('\t'))
      .join('\n');

    try {
      // Copiar para Ã¡rea de transferÃªncia
      await navigator.clipboard.writeText(tsvContent);
      
      // Abrir Google Sheets em nova aba
      window.open('https://docs.google.com/spreadsheets/create', '_blank');
      
      // Mostrar mensagem de instruÃ§Ã£o
      alert('âœ… Dados copiados para a Ã¡rea de transferÃªncia!\n\nðŸ“‹ No Google Sheets que acabou de abrir:\n1. Clique na cÃ©lula A1\n2. Pressione Ctrl+V (ou Cmd+V no Mac) para colar os dados\n3. Os dados serÃ£o organizados automaticamente nas colunas');
    } catch (err) {
      console.error('Erro ao copiar dados:', err);
      alert('âŒ Erro ao copiar dados. Tente novamente ou use a opÃ§Ã£o CSV.');
    }
  };

  const MetricCard = ({ title, value, subtitle, icon: Icon, color, trend, highlight }) => (
    <div className={`bg-white p-6 rounded-lg shadow-md hover:shadow-lg transition-all ${highlight ? 'ring-2 ring-blue-500' : ''}`}>
      <div className="flex items-center justify-between mb-4">
        <div className={`p-3 rounded-lg ${color}`}>
          <Icon className="w-6 h-6" />
        </div>
        {trend !== undefined && (
          <div className={`flex items-center gap-1 text-sm font-medium ${
            trend >= 0 ? 'text-green-600' : 'text-red-600'
          }`}>
            {trend >= 0 ? <ArrowUpRight className="w-4 h-4" /> : <ArrowDownRight className="w-4 h-4" />}
            {Math.abs(trend).toFixed(1)}%
          </div>
        )}
      </div>
      <p className="text-sm text-gray-600 mb-1">{title}</p>
      <p className="text-2xl font-bold text-gray-900">{value}</p>
      {subtitle && <p className="text-xs text-gray-500 mt-1">{subtitle}</p>}
    </div>
  );

  return (
    <div className="space-y-6">
      {/* Header com Filtros */}
      <div className="flex items-center justify-between">
        <div>
          <h2 className="text-2xl font-bold text-gray-900">VisÃ£o Geral Financeira</h2>
          <p className="text-sm text-gray-600 mt-1">Acompanhamento em tempo real</p>
        </div>
        <div className="flex gap-2">
          <button
            onClick={() => setPeriodo('mes')}
            className={`px-4 py-2 rounded-lg font-medium transition-colors ${
              periodo === 'mes' ? 'bg-blue-600 text-white' : 'bg-white text-gray-700 hover:bg-gray-100'
            }`}
          >
            MÃªs
          </button>
          <button
            onClick={() => setPeriodo('trimestre')}
            className={`px-4 py-2 rounded-lg font-medium transition-colors ${
              periodo === 'trimestre' ? 'bg-blue-600 text-white' : 'bg-white text-gray-700 hover:bg-gray-100'
            }`}
          >
            Trimestre
          </button>
          <button
            onClick={() => setPeriodo('ano')}
            className={`px-4 py-2 rounded-lg font-medium transition-colors ${
              periodo === 'ano' ? 'bg-blue-600 text-white' : 'bg-white text-gray-700 hover:bg-gray-100'
            }`}
          >
            Ano
          </button>
          <div className="flex gap-2 ml-4">
            <button
              onClick={exportarDashboard}
              className="flex items-center gap-2 px-4 py-2 bg-green-600 text-white rounded-lg hover:bg-green-700 transition-colors"
              title="Exportar dados para CSV"
            >
              <Download className="w-4 h-4" />
              CSV
            </button>
            <button
              onClick={exportarParaGoogleSheets}
              className="flex items-center gap-2 px-4 py-2 bg-blue-600 text-white rounded-lg hover:bg-blue-700 transition-colors"
              title="Abrir dados no Google Sheets"
            >
              <svg className="w-4 h-4" viewBox="0 0 24 24" fill="currentColor">
                <path d="M11.318 12.545H7.91v-1.909h3.41v1.91zM14.728 0H5.272C3.512 0 2.08.7 2.08 2.182v19.636C2.08 23.3 3.512 24 5.272 24h14.456C21.488 24 22.92 23.3 22.92 21.818V7.636L14.728 0zm6.816 21.818c0 .4-.336.727-.752.727H5.272c-.416 0-.752-.327-.752-.727V2.182c0-.4.336-.727.752-.727h8.08v2.91c0 .4.336.727.752.727h2.912v16.727z"/>
              </svg>
              Sheets
            </button>
          </div>
        </div>
      </div>

      {/* KPIs Principais */}
      <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-4 gap-6">
        <MetricCard
          title="Receita Realizada"
          value={formatMoney(metricas.receitaRealizada)}
          subtitle={`${((metricas.receitaRealizada / metricas.receitaPrevista) * 100 || 0).toFixed(1)}% do previsto`}
          icon={DollarSign}
          color="bg-green-100 text-green-600"
          highlight
        />
        
        <MetricCard
          title="Lucro Operacional"
          value={formatMoney(metricas.lucroOperacional)}
          subtitle={`Margem: ${metricas.margemLucro.toFixed(1)}%`}
          icon={TrendingUp}
          color={metricas.lucroOperacional >= 0 ? "bg-blue-100 text-blue-600" : "bg-red-100 text-red-600"}
        />
        
        <MetricCard
          title="Receita Pendente"
          value={formatMoney(metricas.receitaPendente)}
          subtitle="A receber"
          icon={Clock}
          color="bg-yellow-100 text-yellow-600"
        />
        
        <MetricCard
          title="Despesas Totais"
          value={formatMoney(metricas.despesasTotais)}
          subtitle="MÃªs atual"
          icon={CreditCard}
          color="bg-red-100 text-red-600"
        />
      </div>

      {/* MÃ©tricas SecundÃ¡rias */}
      <div className="grid grid-cols-1 md:grid-cols-2 gap-6">
        <MetricCard
          title="Taxa de InadimplÃªncia"
          value={`${metricas.inadimplencia.toFixed(1)}%`}
          subtitle={`${metricas.clientesInadimplentes} clientes inadimplentes`}
          icon={AlertCircle}
          color={metricas.inadimplencia > 10 ? "bg-red-100 text-red-600" : "bg-blue-100 text-blue-600"}
        />

        <MetricCard
          title="Clientes Ativos"
          value={metricas.clientesAtivos}
          subtitle="Com contratos vigentes"
          icon={Briefcase}
          color="bg-teal-100 text-teal-600"
        />
      </div>

      {/* Linha 2: AnÃ¡lises Detalhadas */}
      <div className="grid grid-cols-1 lg:grid-cols-2 gap-6">
        {/* Fluxo de Caixa */}
        <div className="bg-white p-6 rounded-lg shadow-md">
          <div className="flex items-center gap-3 mb-4">
            <Wallet className="w-6 h-6 text-blue-600" />
            <h3 className="text-lg font-bold text-gray-900">Fluxo de Caixa</h3>
          </div>
          <div className="space-y-4">
            <div>
              <div className="flex justify-between text-sm mb-2">
                <span className="text-gray-600">Entradas</span>
                <span className="font-bold text-green-600">{formatMoney(metricas.receitaRealizada)}</span>
              </div>
              <div className="w-full bg-gray-200 rounded-full h-2">
                <div 
                  className="bg-green-500 h-2 rounded-full" 
                  style={{ width: `${Math.min((metricas.receitaRealizada / (metricas.receitaRealizada + metricas.despesasTotais)) * 100, 100)}%` }} 
                />
              </div>
            </div>
            <div>
              <div className="flex justify-between text-sm mb-2">
                <span className="text-gray-600">SaÃ­das</span>
                <span className="font-bold text-red-600">{formatMoney(metricas.despesasTotais)}</span>
              </div>
              <div className="w-full bg-gray-200 rounded-full h-2">
                <div 
                  className="bg-red-500 h-2 rounded-full" 
                  style={{ width: `${Math.min((metricas.despesasTotais / (metricas.receitaRealizada + metricas.despesasTotais)) * 100, 100)}%` }} 
                />
              </div>
            </div>
            <div className="pt-4 border-t border-gray-200">
              <div className="flex justify-between items-center">
                <span className="text-sm font-medium text-gray-700">Saldo Final</span>
                <span className={`text-xl font-bold ${metricas.fluxoCaixa >= 0 ? 'text-green-600' : 'text-red-600'}`}>
                  {formatMoney(metricas.fluxoCaixa)}
                </span>
              </div>
            </div>
          </div>
        </div>

        {/* PrevisÃ£o PrÃ³ximo MÃªs */}
        <div className="bg-gradient-to-br from-blue-50 to-indigo-50 p-6 rounded-lg border border-blue-200">
          <div className="flex items-center justify-between mb-4">
            <div className="flex items-center gap-3">
              <Calendar className="w-6 h-6 text-blue-600" />
              <h3 className="text-lg font-bold text-blue-900">PrevisÃ£o PrÃ³ximo MÃªs</h3>
            </div>
          </div>
          <p className="text-3xl font-bold text-blue-900 mb-2">
            {formatMoney(metricas.previsaoProximoMes)}
          </p>
          <p className="text-sm text-blue-700 mb-4">
            Receita prevista com vencimentos
          </p>
          <div className="bg-white/50 rounded-lg p-3">
            <div className="flex items-center justify-between mb-1">
              <p className="text-xs text-blue-800 font-medium">Meta Mensal</p>
              <button
                onClick={() => setModalMeta(true)}
                className="text-blue-600 hover:text-blue-800"
                title="Editar meta"
              >
                <Edit2 className="w-3 h-3" />
              </button>
            </div>
            <p className="text-lg font-bold text-blue-900">{formatMoney(metaMensal)}</p>
            <div className="mt-2 w-full bg-blue-200 rounded-full h-2">
              <div 
                className="bg-blue-600 h-2 rounded-full transition-all"
                style={{ width: `${Math.min((metricas.receitaRealizada / metaMensal) * 100, 100)}%` }}
              />
            </div>
            <p className="text-xs text-blue-700 mt-1">
              {((metricas.receitaRealizada / metaMensal) * 100).toFixed(1)}% atingido
            </p>
          </div>
        </div>
      </div>

      {/* DistribuiÃ§Ã£o de Despesas */}
      <div className="grid grid-cols-1 lg:grid-cols-2 gap-6">
        <div className="bg-white p-6 rounded-lg shadow-md">
          <div className="flex items-center justify-between mb-4">
            <div className="flex items-center gap-3">
              <BarChart3 className="w-6 h-6 text-orange-600" />
              <h3 className="text-lg font-bold text-gray-900">DistribuiÃ§Ã£o de Despesas</h3>
            </div>
            <button
              onClick={() => setModalDespesas(true)}
              className="flex items-center gap-1 text-sm bg-orange-100 text-orange-700 px-3 py-1 rounded-lg hover:bg-orange-200 transition-colors"
            >
              <Edit2 className="w-3 h-3" />
              Editar
            </button>
          </div>
          <div className="space-y-3">
            {despesas.length > 0 ? despesas.map((despesa, index) => (
              <div key={index}>
                <div className="flex justify-between text-sm mb-1">
                  <span className="text-gray-700">{despesa.categoria}</span>
                  <span className="font-medium">{formatMoney(despesa.valor)}</span>
                </div>
                <div className="w-full bg-gray-200 rounded-full h-2">
                  <div 
                    className={`h-2 rounded-full ${despesa.tipo === 'fixa' ? 'bg-orange-500' : 'bg-blue-500'}`}
                    style={{ width: `${(despesa.valor / metricas.despesasTotais) * 100}%` }}
                  />
                </div>
                <p className="text-xs text-gray-500 mt-1">
                  {((despesa.valor / metricas.despesasTotais) * 100).toFixed(1)}% do total â€¢ {despesa.tipo === 'fixa' ? 'Fixa' : 'VariÃ¡vel'}
                </p>
              </div>
            )) : (
              <p className="text-gray-500 text-center py-4">Nenhuma despesa cadastrada</p>
            )}
          </div>
        </div>

        {/* SaÃºde Financeira */}
        <div className="bg-white p-6 rounded-lg shadow-md border-l-4 border-green-500">
          <h3 className="text-lg font-bold text-gray-900 mb-4">Indicadores de SaÃºde</h3>
          
          <div className="space-y-4">
            <div>
              <div className="flex justify-between text-sm mb-2">
                <span className="text-gray-600">Margem de Lucro</span>
                <span className="font-medium">{metricas.margemLucro.toFixed(1)}%</span>
              </div>
              <div className="w-full bg-gray-200 rounded-full h-2">
                <div 
                  className={`h-2 rounded-full ${metricas.margemLucro >= 20 ? 'bg-green-600' : metricas.margemLucro >= 10 ? 'bg-yellow-600' : 'bg-red-600'}`}
                  style={{ width: `${Math.min(Math.abs(metricas.margemLucro) * 2, 100)}%` }}
                />
              </div>
            </div>

            <div>
              <div className="flex justify-between text-sm mb-2">
                <span className="text-gray-600">Taxa de Recebimento</span>
                <span className="font-medium">
                  {((metricas.receitaRealizada / metricas.receitaPrevista) * 100 || 0).toFixed(1)}%
                </span>
              </div>
              <div className="w-full bg-gray-200 rounded-full h-2">
                <div 
                  className="bg-blue-600 h-2 rounded-full"
                  style={{ width: `${(metricas.receitaRealizada / metricas.receitaPrevista) * 100 || 0}%` }}
                />
              </div>
            </div>

            <div>
              <div className="flex justify-between text-sm mb-2">
                <span className="text-gray-600">InadimplÃªncia</span>
                <span className="font-medium">{metricas.inadimplencia.toFixed(1)}%</span>
              </div>
              <div className="w-full bg-gray-200 rounded-full h-2">
                <div 
                  className={`h-2 rounded-full ${metricas.inadimplencia > 10 ? 'bg-red-600' : 'bg-green-600'}`}
                  style={{ width: `${Math.min(metricas.inadimplencia * 5, 100)}%` }}
                />
              </div>
            </div>

            <div className="pt-4 border-t border-gray-200">
              <div className="flex items-center gap-2">
                <CheckCircle className={`w-5 h-5 ${
                  metricas.margemLucro >= 15 && metricas.inadimplencia < 10
                    ? 'text-green-600'
                    : 'text-yellow-600'
                }`} />
                <span className={`text-sm font-medium ${
                  metricas.margemLucro >= 15 && metricas.inadimplencia < 10
                    ? 'text-green-600'
                    : 'text-yellow-600'
                }`}>
                  {metricas.margemLucro >= 15 && metricas.inadimplencia < 10
                    ? 'Empresa financeiramente saudÃ¡vel'
                    : 'AtenÃ§Ã£o aos indicadores'}
                </span>
              </div>
            </div>
          </div>
        </div>
      </div>

      {/* Alertas e RecomendaÃ§Ãµes */}
      <div className="space-y-4">
        {metricas.inadimplencia > 10 && (
          <div className="bg-red-50 border-l-4 border-red-500 p-4 rounded-lg">
            <div className="flex items-start gap-3">
              <AlertCircle className="w-6 h-6 text-red-600 flex-shrink-0 mt-0.5" />
              <div>
                <h4 className="text-red-800 font-bold mb-1">Alta Taxa de InadimplÃªncia</h4>
                <p className="text-red-700 text-sm">
                  {metricas.clientesInadimplentes} clientes com pagamentos atrasados. Recomenda-se intensificar a cobranÃ§a usando o sistema de email marketing.
                </p>
              </div>
            </div>
          </div>
        )}

        {metricas.margemLucro < 10 && metricas.receitaRealizada > 0 && (
          <div className="bg-yellow-50 border-l-4 border-yellow-500 p-4 rounded-lg">
            <div className="flex items-start gap-3">
              <AlertCircle className="w-6 h-6 text-yellow-600 flex-shrink-0 mt-0.5" />
              <div>
                <h4 className="text-yellow-800 font-bold mb-1">Margem de Lucro Baixa</h4>
                <p className="text-yellow-700 text-sm">
                  Margem atual de {metricas.margemLucro.toFixed(1)}%. Considere revisar preÃ§os dos serviÃ§os ou reduzir custos operacionais.
                </p>
              </div>
            </div>
          </div>
        )}

        {metricas.lucroOperacional > 0 && metricas.inadimplencia < 10 && metricas.margemLucro >= 15 && (
          <div className="bg-green-50 border-l-4 border-green-500 p-4 rounded-lg">
            <div className="flex items-start gap-3">
              <CheckCircle className="w-6 h-6 text-green-600 flex-shrink-0 mt-0.5" />
              <div>
                <h4 className="text-green-800 font-bold mb-1">Desempenho Excelente</h4>
                <p className="text-green-700 text-sm">
                  Seus indicadores financeiros estÃ£o saudÃ¡veis. Continue monitorando e considere expansÃ£o de serviÃ§os.
                </p>
              </div>
            </div>
          </div>
        )}
      </div>

      {/* Modal para editar despesas */}
      {modalDespesas && (
        <div className="fixed inset-0 bg-black bg-opacity-50 flex items-center justify-center z-50">
          <div className="bg-white rounded-lg p-6 w-full max-w-md max-h-[80vh] overflow-y-auto">
            <div className="flex items-center justify-between mb-4">
              <h3 className="text-lg font-bold text-gray-900">Gerenciar Despesas</h3>
              <button
                onClick={() => setModalDespesas(false)}
                className="text-gray-400 hover:text-gray-600"
              >
                <X className="w-6 h-6" />
              </button>
            </div>

            {/* FormulÃ¡rio para adicionar/editar */}
            <div className="space-y-4 mb-6">
              <div>
                <label className="block text-sm font-medium text-gray-700 mb-1">
                  Categoria
                </label>
                <input
                  type="text"
                  value={novaDespesa.categoria}
                  onChange={(e) => setNovaDespesa({...novaDespesa, categoria: e.target.value})}
                  className="w-full px-3 py-2 border border-gray-300 rounded-lg focus:ring-2 focus:ring-blue-500 focus:border-transparent"
                  placeholder="Ex: Aluguel, Marketing..."
                />
              </div>
              <div>
                <label className="block text-sm font-medium text-gray-700 mb-1">
                  Valor (R$)
                </label>
                <input
                  type="text"
                  value={formatInputCurrency(novaDespesa.valor)}
                  onChange={(e) => {
                    let valor = e.target.value.replace(/\D/g, '');
                    valor = valor.substring(0, 10);
                    
                    // Permite que o usuÃ¡rio digite apenas "0"
                    if (valor === '') {
                      setNovaDespesa({...novaDespesa, valor: ''});
                    } else if (valor === '0') {
                      setNovaDespesa({...novaDespesa, valor: '0'});
                    } else {
                      // Remove zeros Ã  esquerda (exceto se for "0")
                      valor = valor.replace(/^0+/, '');
                      setNovaDespesa({...novaDespesa, valor: valor});
                    }
                  }}
                  onKeyDown={(e) => {
                    // Permite apagar completamente o campo
                    if (e.key === 'Backspace' && novaDespesa.valor === '0') {
                      setNovaDespesa({...novaDespesa, valor: ''});
                    }
                  }}
                  className="w-full px-3 py-2 border border-gray-300 rounded-lg focus:ring-2 focus:ring-blue-500 focus:border-transparent"
                  placeholder="R$ 0,00"
                />
              </div>
              <div>
                <label className="block text-sm font-medium text-gray-700 mb-1">
                  Tipo
                </label>
                <select
                  value={novaDespesa.tipo}
                  onChange={(e) => setNovaDespesa({...novaDespesa, tipo: e.target.value})}
                  className="w-full px-3 py-2 border border-gray-300 rounded-lg focus:ring-2 focus:ring-blue-500 focus:border-transparent"
                >
                  <option value="fixa">Fixa</option>
                  <option value="variavel">VariÃ¡vel</option>
                </select>
              </div>
              <div className="flex gap-2">
                {editandoDespesa !== null ? (
                  <>
                    <button
                      onClick={salvarEdicaoDespesa}
                      className="flex-1 bg-blue-600 text-white px-4 py-2 rounded-lg hover:bg-blue-700 transition-colors"
                    >
                      <Save className="w-4 h-4 inline mr-1" />
                      Salvar
                    </button>
                    <button
                      onClick={cancelarEdicao}
                      className="px-4 py-2 border border-gray-300 text-gray-700 rounded-lg hover:bg-gray-50 transition-colors"
                    >
                      Cancelar
                    </button>
                  </>
                ) : (
                  <button
                    onClick={adicionarDespesa}
                    className="flex-1 bg-green-600 text-white px-4 py-2 rounded-lg hover:bg-green-700 transition-colors"
                  >
                    <Plus className="w-4 h-4 inline mr-1" />
                    Adicionar
                  </button>
                )}
              </div>
            </div>

            {/* Lista de despesas */}
            <div className="space-y-2">
              <h4 className="font-medium text-gray-900 mb-2">Despesas Cadastradas</h4>
              {despesas.map((despesa, index) => (
                <div key={index} className="flex items-center justify-between p-3 bg-gray-50 rounded-lg">
                  <div>
                    <p className="font-medium text-gray-900">{despesa.categoria}</p>
                    <p className="text-sm text-gray-600">
                      {formatMoney(despesa.valor)} â€¢ {despesa.tipo === 'fixa' ? 'Fixa' : 'VariÃ¡vel'}
                    </p>
                  </div>
                  <div className="flex gap-1">
                    <button
                      onClick={() => editarDespesa(index)}
                      className="p-1 text-blue-600 hover:bg-blue-100 rounded"
                      title="Editar"
                    >
                      <Edit2 className="w-4 h-4" />
                    </button>
                    <button
                      onClick={() => removerDespesa(index)}
                      className="p-1 text-red-600 hover:bg-red-100 rounded"
                      title="Remover"
                    >
                      <Trash2 className="w-4 h-4" />
                    </button>
                  </div>
                </div>
              ))}
            </div>
          </div>
        </div>
      )}

      {/* Modal para editar meta */}
      {modalMeta && (
        <div className="fixed inset-0 bg-black bg-opacity-50 flex items-center justify-center z-50">
          <div className="bg-white rounded-lg p-6 w-full max-w-sm">
            <div className="flex items-center justify-between mb-4">
              <h3 className="text-lg font-bold text-gray-900">Editar Meta Mensal</h3>
              <button
                onClick={() => setModalMeta(false)}
                className="text-gray-400 hover:text-gray-600"
              >
                <X className="w-6 h-6" />
              </button>
            </div>

            <div className="space-y-4">
              <div>
                <label className="block text-sm font-medium text-gray-700 mb-1">
                  Meta Mensal (R$)
                </label>
                <input
                  type="text"
                  value={metaMensal === 0 ? 'R$ 0,00' : formatInputCurrency(Math.round(metaMensal * 100).toString())}
                  onChange={(e) => {
                    let valor = e.target.value.replace(/\D/g, '');
                    valor = valor.substring(0, 10);
                    
                    if (valor === '') {
                      setMetaMensal(0);
                    } else if (valor === '0') {
                      setMetaMensal(0);
                    } else {
                      // Remove zeros Ã  esquerda e converte para nÃºmero
                      valor = valor.replace(/^0+/, '');
                      setMetaMensal(parseFloat(valor) / 100);
                    }
                  }}
                  className="w-full px-3 py-2 border border-gray-300 rounded-lg focus:ring-2 focus:ring-blue-500 focus:border-transparent"
                  placeholder="R$ 0,00"
                />
              </div>
              <div className="flex gap-2">
                <button
                  onClick={() => setModalMeta(false)}
                  className="flex-1 bg-blue-600 text-white px-4 py-2 rounded-lg hover:bg-blue-700 transition-colors"
                >
                  <Save className="w-4 h-4 inline mr-1" />
                  Salvar
                </button>
                <button
                  onClick={() => setModalMeta(false)}
                  className="px-4 py-2 border border-gray-300 text-gray-700 rounded-lg hover:bg-gray-50 transition-colors"
                >
                  Cancelar
                </button>
              </div>
            </div>
          </div>
        </div>
      )}
    </div>
  );
};

export default DashboardFinanceiro;
