          <button
            onClick={() => setAbaAtiva('dashboard')}
            className={`flex items-center gap-2 px-6 py-3 rounded-lg transition-all duration-200 font-medium ${
              abaAtiva === 'dashboard'
                ? 'bg-blue-600 text-white shadow-md'
                : 'bg-gray-100 text-gray-700 hover:bg-gray-200'
            }`}
          >
            <DollarSign className="w-5 h-5" />
            Dashboard
          </button>
          
          <button
            onClick={() => setAbaAtiva('clientes')}
            className={`flex items-center gap-2 px-6 py-3 rounded-lg transition-all duration-200 font-medium ${
              abaAtiva === 'clientes'
                ? 'bg-blue-600 text-white shadow-md'
                : 'bg-gray-100 text-gray-700 hover:bg-gray-200'
            }`}
          >
            <Users className="w-5 h-5" />
            Clientes
          </button>

        </div>

        {/* CONTE√öDO DAS ABAS */}
        {abaAtiva === 'dashboard' && (
          <DashboardAprimorado clientes={clientes} />
        )}

        {abaAtiva === 'clientes' && (
          <>
            {/* Cards de Resumo */}
            <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-4 gap-6 mb-8">
              <div className="bg-white p-6 rounded-lg shadow-md hover:shadow-lg transition-shadow border-l-4 border-blue-500">
                <div className="flex items-center justify-between">
                  <div>
                    <p className="text-sm text-gray-600 mb-1">Total a Receber</p>
                    <p className="text-2xl font-bold text-blue-600">{formatarMoeda(totais.totalAReceber)}</p>
                  </div>
                  <DollarSign className="w-8 h-8 text-blue-600" />
                </div>
              </div>
              
              <div className="bg-white p-6 rounded-lg shadow-md hover:shadow-lg transition-shadow border-l-4 border-green-500">
                <div className="flex items-center justify-between">
                  <div>
                    <p className="text-sm text-gray-600 mb-1">Total Recebido</p>
                    <p className="text-2xl font-bold text-green-600">{formatarMoeda(totais.totalRecebido)}</p>
                  </div>
                  <CheckCircle className="w-8 h-8 text-green-600" />
                </div>
              </div>
              
              <div className="bg-white p-6 rounded-lg shadow-md hover:shadow-lg transition-shadow border-l-4 border-red-500">
                <div className="flex items-center justify-between">
                  <div>
                    <p className="text-sm text-gray-600 mb-1">Clientes em Atraso</p>
                    <p className="text-2xl font-bold text-red-600">{totais.clientesEmAtraso}</p>
                  </div>
                  <AlertCircle className="w-8 h-8 text-red-600" />
                </div>
              </div>
              
              <div className="bg-white p-6 rounded-lg shadow-md hover:shadow-lg transition-shadow border-l-4 border-gray-500">
                <div className="flex items-center justify-between">
                  <div>
                    <p className="text-sm text-gray-600 mb-1">Total de Clientes</p>
                    <p className="text-2xl font-bold text-gray-700">{totais.totalClientes}</p>
                  </div>
                  <Users className="w-8 h-8 text-gray-700" />
                </div>
              </div>
            </div>

            {/* Bot√µes de Exportar */}
            <div className="bg-white p-6 rounded-lg shadow-md mb-6 border-b-4 border-emerald-500">
              <div className="flex flex-col gap-4">
                <div className="flex items-center justify-between">
                  <div className="flex items-center gap-3">
                    <div className="p-2 bg-emerald-100 rounded-lg">
                      <Download className="w-5 h-5 text-emerald-600" />
                    </div>
                    <div>
                      <h3 className="font-bold text-slate-800">Exportar Dados</h3>
                      <p className="text-sm text-gray-600">Baixe os dados em CSV ou abra no Google Sheets</p>
                    </div>
                  </div>
                </div>
                <div className="flex gap-2 items-center flex-wrap">
                  <button
                    onClick={exportarRelatorio}
                    className="flex items-center gap-2 px-4 py-2 bg-emerald-500 text-white rounded-lg hover:bg-emerald-600 transition-all duration-200 shadow-md hover:shadow-lg text-sm font-medium"
                    title="Exportar dados para CSV"
                  >
                    <Download className="w-4 h-4" />
                    CSV
                  </button>
                  <button
                    onClick={exportarGoogleSheets}
                    className="flex items-center gap-2 px-4 py-2 bg-slate-600 text-white rounded-lg hover:bg-slate-700 transition-all duration-200 shadow-md hover:shadow-lg text-sm font-medium"
                    title="Abrir dados no Google Sheets"
                  >
                    <svg className="w-4 h-4" viewBox="0 0 24 24" fill="currentColor">
                      <path d="M11.318 12.545H7.91v-1.909h3.41v1.91zM14.728 0H5.272C3.512 0 2.08.7 2.08 2.182v19.636C2.08 23.3 3.512 24 5.272 24h14.456C21.488 24 22.92 23.3 22.92 21.818V7.636L14.728 0zm6.816 21.818c0 .4-.336.727-.752.727H5.272c-.416 0-.752-.327-.752-.727V2.182c0-.4.336-.727.752-.727h8.08v2.91c0 .4.336.727.752.727h2.912v16.727z"/>
                    </svg>
                    Sheets
                  </button>
                </div>
              </div>
            </div>

            {/* Email Marketing Section */}
            <div className="bg-white rounded-lg shadow-md p-6 mb-6">
              <EmailManager clientes={clientes} />
            </div>

            {/* Filtros e Busca */}
        <div className="bg-white p-6 rounded-lg shadow-md mb-6">
          <div className="flex flex-col gap-4">
            <div className="flex items-center gap-3 mb-2">
              <div className="p-2 bg-blue-100 rounded-lg">
                <Search className="w-5 h-5 text-blue-600" />
              </div>
              <h3 className="font-bold text-slate-800">Pesquisar e Filtrar</h3>
            </div>
            <div className="flex flex-col md:flex-row gap-4">
              <div className="relative flex-1 max-w-md">
                <Search className="w-5 h-5 absolute left-4 top-4 text-slate-400" />
                <input
                  type="text"
                  placeholder="Buscar por nome ou empresa..."
                  className="w-full px-10 py-3 border border-gray-300 rounded-lg focus:ring-2 focus:ring-blue-500 text-gray-900"
                  value={filtros.busca}
                  onChange={(e) => setFiltros({...filtros, busca: e.target.value})}
                />
              </div>
              
              <div className="flex items-center gap-3">
                <div className="p-2 bg-slate-500/10 rounded-lg">
                  <Filter className="w-5 h-5 text-slate-600" />
                </div>
                <select
                  className="px-4 py-3 border border-gray-300 rounded-lg focus:ring-2 focus:ring-blue-500 text-gray-900"
                  value={filtros.status}
                  onChange={(e) => setFiltros({...filtros, status: e.target.value})}
                >
                  <option value="todos">Todos os Status</option>
                  <option value="pendente">Pendente</option>
                  <option value="em_atraso">Em Atraso</option>
                  <option value="pago">Pago</option>
                </select>
              </div>
            </div>
            
            <button
              onClick={() => setModalAberto(true)}
              className="flex items-center justify-center gap-2 bg-gradient-to-r from-blue-600 to-blue-700 text-white px-6 py-3 rounded-lg hover:from-blue-700 hover:to-blue-800 transition-all duration-200 shadow-md hover:shadow-lg w-full md:w-auto font-medium"
            >
              <Plus className="w-5 h-5" />
              Adicionar Novo Cliente
            </button>
          </div>
        </div>

        {/* Lista de Clientes */}
        <div className="bg-white rounded-lg shadow-md overflow-hidden border-t-4 border-blue-500">
          <div className="p-6 border-b border-slate-200 bg-gradient-to-r from-blue-50 to-indigo-50">
            <div className="flex items-center gap-3">
              <div className="p-2 bg-blue-100 rounded-lg">
                <Users className="w-5 h-5 text-blue-600" />
              </div>
              <div>
                <h3 className="font-bold text-slate-800">Listagem de Clientes</h3>
                <p className="text-sm text-gray-600">Total de {clientesFiltrados.length} cliente(s) encontrado(s)</p>
              </div>
            </div>
          </div>
          <div className="overflow-x-auto">
            <table className="w-full">
              <thead className="bg-gradient-to-r from-slate-100 to-slate-50 border-b-2 border-slate-200">
                <tr>
                  <th className="px-6 py-4 text-left text-xs font-bold text-slate-700 uppercase tracking-wider">Cliente</th>
                  <th className="px-6 py-4 text-left text-xs font-bold text-slate-700 uppercase tracking-wider">Contato</th>
                  <th className="px-6 py-4 text-left text-xs font-bold text-slate-700 uppercase tracking-wider">C√≥digo Contrato</th>
                  <th className="px-6 py-4 text-left text-xs font-bold text-slate-700 uppercase tracking-wider">Valores</th>
                  <th className="px-6 py-4 text-left text-xs font-bold text-slate-700 uppercase tracking-wider">Parcelas</th>
                  <th className="px-6 py-4 text-left text-xs font-bold text-slate-700 uppercase tracking-wider">Status</th>
                  <th className="px-6 py-4 text-left text-xs font-bold text-slate-700 uppercase tracking-wider">Pr√≥ximo Venc.</th>
                  <th className="px-6 py-4 text-left text-xs font-bold text-slate-700 uppercase tracking-wider">A√ß√µes</th>
                </tr>
              </thead>
              <tbody className="divide-y divide-slate-200">
                {clientesFiltrados.map((cliente) => {
                  const status = calcularStatus(cliente);
                  const diasAtraso = calcularDiasAtraso(cliente.proximoVencimento);
                  
                  return (
                    <tr key={cliente.id} className="hover:bg-blue-50/50 transition-all duration-200 border-l-4 border-transparent hover:border-l-blue-400">
                      <td className="px-6 py-4 whitespace-nowrap">
                        <div>
                          <div className="text-sm font-bold text-slate-900">{cliente.nomeResponsavel}</div>
                          <div className="text-sm text-slate-600 font-medium">{cliente.nomeEmpresa}</div>
                        </div>
                      </td>
                      <td className="px-6 py-4 whitespace-nowrap text-sm text-gray-900">
                        <div>
                          <div>{cliente.email}</div>
                          <div className="text-gray-500 text-xs">{cliente.telefone}</div>
                        </div>
                      </td>
                      <td className="px-6 py-4 whitespace-nowrap text-sm font-semibold text-gray-900">
                        {cliente.codigoContrato}
                      </td>
                      <td className="px-6 py-4 whitespace-nowrap">
                        <div className="text-sm text-gray-900 space-y-1">
                          <div className="font-semibold">Total: {formatarMoeda(cliente.valorTotal)}</div>
                          <div className="text-green-600 text-xs">Pago: {formatarMoeda(cliente.valorPago)}</div>
                          <div className="text-red-600 text-xs">Restante: {formatarMoeda(cliente.valorTotal - cliente.valorPago)}</div>
                        </div>
                      </td>
                      <td className="px-6 py-4 whitespace-nowrap text-sm text-gray-900">
                        <div className="flex items-center">
                          <div className="w-16 bg-gray-200 rounded-full h-2 mr-2">
                            <div 
                              className="bg-blue-600 h-2 rounded-full" 
                              style={{width: `${Math.min((cliente.parcelasPagas / cliente.parcelas) * 100, 100)}%`}}
                            ></div>
                          </div>
                          <span>{cliente.parcelasPagas}/{cliente.parcelas}</span>
                        </div>
                        <div className="text-xs text-gray-500 mt-1">
                          {formatarMoeda(cliente.valorParcela)} cada
                        </div>
                      </td>
                      <td className="px-6 py-4 whitespace-nowrap">
                        <span className={`inline-flex items-center gap-1 px-2 py-1 rounded-full text-xs font-medium ${getStatusColor(status)}`}>
                          {getStatusIcon(status)}
                          {getStatusText(status)}
                          {status === 'em_atraso' && diasAtraso > 0 && (
                            <span className="ml-1">({diasAtraso}d)</span>
                          )}
                        </span>
                      </td>
                      <td className="px-6 py-4 whitespace-nowrap text-sm text-gray-900">
                        {cliente.proximoVencimento ? formatarData(cliente.proximoVencimento) : '-'}
                      </td>
                      <td className="px-6 py-4 whitespace-nowrap text-sm font-medium">
                        <div className="flex items-center gap-2">
                          <button
                            onClick={() => {
                              setClienteSelecionado(cliente);
                              setModalDetalhes(true);
                            }}
                            className="text-blue-600 hover:text-blue-900"
                            title="Ver detalhes"
                          >
                            <Eye className="w-4 h-4" />
                          </button>
                          
                          {status !== 'pago' && (
                            <button
                              onClick={() => {
                                setClienteSelecionado(cliente);
                                setPagamentoForm({
                                  valor: cliente.valorParcela.toString(),
                                  data: new Date().toISOString().split('T')[0],
                                  descricao: `${cliente.parcelasPagas + 1}¬™ parcela`
                                });
                                setModalPagamento(true);
                              }}
                              className="text-green-600 hover:text-green-900"
                              title="Registrar pagamento"
                            >
                              <DollarSign className="w-4 h-4" />
                            </button>
                          )}
                          
                          <button
                            onClick={() => {
                              setClienteEditando({...cliente});
                              setModalAberto(true);
                            }}
                            className="text-yellow-600 hover:text-yellow-900"
                            title="Editar cliente"
                          >
                            <Edit className="w-4 h-4" />
                          </button>
                          
                          <button
                            onClick={() => {
                              setClienteSelecionado(cliente);
                              setModalConfirmacao(true);
                            }}
                            className="text-red-600 hover:text-red-900"
                            title="Excluir cliente"
                          >
                            <Trash2 className="w-4 h-4" />
                          </button>
                        </div>
                      </td>
                    </tr>
                  );
                })}
              </tbody>
            </table>
          </div>
          
          {clientesFiltrados.length === 0 && (
            <div className="text-center py-12">
              <Users className="mx-auto h-12 w-12 text-gray-400" />
              <h3 className="mt-2 text-sm font-medium text-gray-900">Nenhum cliente encontrado</h3>
              <p className="mt-1 text-sm text-gray-500">Comece adicionando um novo cliente.</p>
            </div>
          )}
        </div>
        </>
      )}

      {/* Modais */}
      <ModalCliente
        isOpen={modalAberto}
        onClose={fecharModal}
        onSave={clienteEditando ? editarCliente : adicionarCliente}
        clienteEditando={clienteEditando}
        novoCliente={novoCliente}
        setClienteEditando={setClienteEditando}
        setNovoCliente={setNovoCliente}
        formatarCNPJ={formatarCNPJ}
        formatarMoedaInput={formatarMoedaInput}
      />

      <ModalPagamento
        isOpen={modalPagamento}
        onClose={() => setModalPagamento(false)}
        onSave={registrarPagamento}
        pagamentoForm={pagamentoForm}
        setPagamentoForm={setPagamentoForm}
        formatarMoedaInput={formatarMoedaInput}
      />

      <ModalDetalhes
        isOpen={modalDetalhes}
        onClose={() => {
          setModalDetalhes(false);
          setClienteSelecionado(null);
        }}
        cliente={clienteSelecionado}
        formatarMoeda={formatarMoeda}
        formatarData={formatarData}
      />

      <ModalConfirmacao
        isOpen={modalConfirmacao}
        onClose={() => {
          setModalConfirmacao(false);
          setClienteSelecionado(null);
        }}
        onConfirm={excluirCliente}
        cliente={clienteSelecionado}
      />

      <ModalDespesa
        isOpen={modalDespesa}
        onClose={() => setModalDespesa(false)}
        onSave={(novaDespesa) => {
          const novasDespesas = [...despesas, novaDespesa];
          setDespesas(novasDespesas);
          localStorage.setItem('contas-a-pagar', JSON.stringify(novasDespesas));
          localStorage.setItem('dashboard-despesas', JSON.stringify([
            ...JSON.parse(localStorage.getItem('dashboard-despesas') || '[]'),
            {
              categoria: novaDespesa.fornecedor,
              valor: novaDespesa.valor,
              tipo: 'variavel'
            }
          ]));
          setModalDespesa(false);
        }}
      />



  </div>
      </div>
    </div>
  );
};

export default FinancialManager;
