import React, { useState, useEffect } from 'react';
import { useNavigate, useSearchParams } from 'react-router-dom';
import { UserPlus, Mail, Lock, User, Briefcase, Phone, Eye, EyeOff, CheckCircle, AlertCircle } from 'lucide-react';
import { supabase } from '../supabaseClient'; // Ajuste o caminho

const CadastroConvite = () => {
  const [searchParams] = useSearchParams();
  const navigate = useNavigate();
  
  const [convite, setConvite] = useState(null);
  const [validandoConvite, setValidandoConvite] = useState(true);
  const [mostrarSenha, setMostrarSenha] = useState(false);
  const [loading, setLoading] = useState(false);
  
  const [formData, setFormData] = useState({
    senha: '',
    confirmarSenha: '',
    telefone: ''
  });

  const [erros, setErros] = useState({});

  useEffect(() => {
    validarConvite();
  }, []);

  const validarConvite = async () => {
    const token = searchParams.get('convite');
    
    if (!token) {
      alert('Link de convite inválido');
      navigate('/login');
      return;
    }

    try {
      const { data, error } = await supabase
        .from('convites')
        .select('*')
        .eq('token', token)
        .eq('usado', false)
        .single();

      if (error || !data) {
        alert('Convite inválido, já usado ou expirado');
        navigate('/login');
        return;
      }

      // Verificar se não expirou
      const expiraEm = new Date(data.expira_em);
      if (expiraEm < new Date()) {
        alert('Este convite expirou. Solicite um novo convite ao administrador.');
        navigate('/login');
        return;
      }

      setConvite(data);
    } catch (error) {
      console.error('Erro ao validar convite:', error);
      alert('Erro ao validar convite');
      navigate('/login');
    } finally {
      setValidandoConvite(false);
    }
  };

  const validarSenha = (senha) => {
    const erros = [];
    
    if (senha.length < 8) {
      erros.push('Mínimo 8 caracteres');
    }
    if (!/[A-Z]/.test(senha)) {
      erros.push('Uma letra maiúscula');
    }
    if (!/[a-z]/.test(senha)) {
      erros.push('Uma letra minúscula');
    }
    if (!/[0-9]/.test(senha)) {
      erros.push('Um número');
    }
    if (!/[!@#$%^&*(),.?":{}|<>]/.test(senha)) {
      erros.push('Um caractere especial');
    }
    
    return erros;
  };

  const handleCadastro = async (e) => {
    e.preventDefault();
    
    // Validações
    const novosErros = {};
    
    if (formData.senha !== formData.confirmarSenha) {
      novosErros.confirmarSenha = 'As senhas não coincidem';
    }
    
    const errosSenha = validarSenha(formData.senha);
    if (errosSenha.length > 0) {
      novosErros.senha = errosSenha.join(', ');
    }
    
    if (Object.keys(novosErros).length > 0) {
      setErros(novosErros);
      return;
    }
    
    setLoading(true);
    setErros({});

    try {
      // 1. Criar usuário no Supabase Auth
      const { data: authData, error: authError } = await supabase.auth.signUp({
        email: convite.email,
        password: formData.senha,
        options: {
          data: {
            nome_completo: convite.nome_completo,
            departamento: convite.departamento,
            cargo: convite.cargo,
            telefone: formData.telefone
          }
        }
      });

      if (authError) throw authError;

      // 2. Atualizar perfil do usuário
      const { error: perfilError } = await supabase
        .from('perfis_usuarios')
        .update({
          nome_completo: convite.nome_completo,
          departamento: convite.departamento,
          cargo: convite.cargo,
          telefone: formData.telefone
        })
        .eq('id', authData.user.id);

      if (perfilError) console.error('Erro ao atualizar perfil:', perfilError);

      // 3. Marcar convite como usado
      const { error: conviteError } = await supabase
        .from('convites')
        .update({
          usado: true,
          usado_em: new Date().toISOString()
        })
        .eq('token', convite.token);

      if (conviteError) console.error('Erro ao marcar convite:', conviteError);

      // 4. Registrar log de acesso
      const { error: logError } = await supabase
        .from('logs_acesso')
        .insert([{
          user_id: authData.user.id,
          tipo_acao: 'cadastro',
          sucesso: true
        }]);

      if (logError) console.error('Erro ao registrar log:', logError);

      alert('✅ Cadastro realizado com sucesso!\n\nVocê será redirecionado para o login.');
      navigate('/login');

    } catch (error) {
      console.error('Erro no cadastro:', error);
      
      if (error.message.includes('already registered')) {
        alert('Este email já está cadastrado. Faça login.');
        navigate('/login');
      } else {
        alert('Erro ao criar conta. Tente novamente.');
      }
    } finally {
      setLoading(false);
    }
  };

  if (validandoConvite) {
    return (
      <div className="min-h-screen bg-gradient-to-br from-blue-50 to-indigo-100 flex items-center justify-center">
        <div className="text-center">
          <div className="animate-spin rounded-full h-12 w-12 border-b-2 border-blue-600 mx-auto"></div>
          <p className="mt-4 text-gray-600">Validando convite...</p>
        </div>
      </div>
    );
  }

  if (!convite) {
    return null;
  }

  const errosSenha = validarSenha(formData.senha);
  const senhaValida = errosSenha.length === 0 && formData.senha.length > 0;

  return (
    <div className="min-h-screen bg-gradient-to-br from-blue-50 to-indigo-100 flex items-center justify-center p-4">
      <div className="bg-white rounded-2xl shadow-2xl p-8 w-full max-w-md">
        {/* Header */}
        <div className="text-center mb-8">
          <div className="inline-flex items-center justify-center w-16 h-16 bg-blue-100 rounded-full mb-4">
            <UserPlus className="w-8 h-8 text-blue-600" />
          </div>
          <h1 className="text-2xl font-bold text-gray-900 mb-2">
            Bem-vindo à PROTEQ!
          </h1>
          <p className="text-gray-600">Complete seu cadastro</p>
        </div>

        {/* Informações do Convite */}
        <div className="bg-blue-50 border border-blue-200 rounded-lg p-4 mb-6">
          <div className="flex items-start gap-3">
            <CheckCircle className="w-5 h-5 text-blue-600 flex-shrink-0 mt-0.5" />
            <div className="flex-1">
              <p className="text-sm font-medium text-blue-900 mb-1">Convite Validado</p>
              <p className="text-sm text-blue-700">
                <strong>{convite.nome_completo}</strong>
              </p>
              <p className="text-sm text-blue-600">{convite.email}</p>
              {convite.departamento && (
                <p className="text-sm text-blue-600">
                  {convite.departamento} {convite.cargo && `• ${convite.cargo}`}
                </p>
              )}
            </div>
          </div>
        </div>

        {/* Formulário */}
        <form onSubmit={handleCadastro} className="space-y-4">
          {/* Telefone (opcional) */}
          <div>
            <label className="block text-sm font-medium text-gray-700 mb-1">
              Telefone (opcional)
            </label>
            <div className="relative">
              <Phone className="absolute left-3 top-1/2 transform -translate-y-1/2 w-5 h-5 text-gray-400" />
              <input
                type="tel"
                value={formData.telefone}
                onChange={(e) => setFormData({...formData, telefone: e.target.value})}
                placeholder="(00) 00000-0000"
                className="w-full pl-10 pr-4 py-2 border border-gray-300 rounded-lg focus:ring-2 focus:ring-blue-500 focus:border-transparent"
              />
            </div>
          </div>

          {/* Senha */}
          <div>
            <label className="block text-sm font-medium text-gray-700 mb-1">
              Senha *
            </label>
            <div className="relative">
              <Lock className="absolute left-3 top-1/2 transform -translate-y-1/2 w-5 h-5 text-gray-400" />
              <input
                type={mostrarSenha ? 'text' : 'password'}
                value={formData.senha}
                onChange={(e) => setFormData({...formData, senha: e.target.value})}
                className={`w-full pl-10 pr-12 py-2 border rounded-lg focus:ring-2 focus:ring-blue-500 focus:border-transparent ${
                  erros.senha ? 'border-red-500' : 'border-gray-300'
                }`}
                required
              />
              <button
                type="button"
                onClick={() => setMostrarSenha(!mostrarSenha)}
                className="absolute right-3 top-1/2 transform -translate-y-1/2 text-gray-400 hover:text-gray-600"
              >
                {mostrarSenha ? <EyeOff className="w-5 h-5" /> : <Eye className="w-5 h-5" />}
              </button>
            </div>
            {erros.senha && (
              <p className="text-xs text-red-600 mt-1">{erros.senha}</p>
            )}
            
            {/* Indicadores de força da senha */}
            {formData.senha && (
              <div className="mt-2 space-y-1">
                <div className="flex items-center gap-2 text-xs">
                  <div className={`w-2 h-2 rounded-full ${formData.senha.length >= 8 ? 'bg-green-500' : 'bg-gray-300'}`} />
                  <span className={formData.senha.length >= 8 ? 'text-green-600' : 'text-gray-500'}>
                    Mínimo 8 caracteres
                  </span>
                </div>
                <div className="flex items-center gap-2 text-xs">
                  <div className={`w-2 h-2 rounded-full ${/[A-Z]/.test(formData.senha) ? 'bg-green-500' : 'bg-gray-300'}`} />
                  <span className={/[A-Z]/.test(formData.senha) ? 'text-green-600' : 'text-gray-500'}>
                    Uma letra maiúscula
                  </span>
                </div>
                <div className="flex items-center gap-2 text-xs">
                  <div className={`w-2 h-2 rounded-full ${/[0-9]/.test(formData.senha) ? 'bg-green-500' : 'bg-gray-300'}`} />
                  <span className={/[0-9]/.test(formData.senha) ? 'text-green-600' : 'text-gray-500'}>
                    Um número
                  </span>
                </div>
                <div className="flex items-center gap-2 text-xs">
                  <div className={`w-2 h-2 rounded-full ${/[!@#$%^&*(),.?":{}|<>]/.test(formData.senha) ? 'bg-green-500' : 'bg-gray-300'}`} />
                  <span className={/[!@#$%^&*(),.?":{}|<>]/.test(formData.senha) ? 'text-green-600' : 'text-gray-500'}>
                    Um caractere especial
                  </span>
                </div>
              </div>
            )}
          </div>

          {/* Confirmar Senha */}
          <div>
            <label className="block text-sm font-medium text-gray-700 mb-1">
              Confirmar Senha *
            </label>
            <div className="relative">
              <Lock className="absolute left-3 top-1/2 transform -translate-y-1/2 w-5 h-5 text-gray-400" />
              <input
                type={mostrarSenha ? 'text' : 'password'}
                value={formData.confirmarSenha}
                onChange={(e) => setFormData({...formData, confirmarSenha: e.target.value})}
                className={`w-full pl-10 pr-4 py-2 border rounded-lg focus:ring-2 focus:ring-blue-500 focus:border-transparent ${
                  erros.confirmarSenha ? 'border-red-500' : 'border-gray-300'
                }`}
                required
              />
            </div>
            {erros.confirmarSenha && (
              <p className="text-xs text-red-600 mt-1">{erros.confirmarSenha}</p>
            )}
            {formData.confirmarSenha && formData.senha === formData.confirmarSenha && (
              <p className="text-xs text-green-600 mt-1 flex items-center gap-1">
                <CheckCircle className="w-3 h-3" />
                As senhas coincidem
              </p>
            )}
          </div>

          {/* Botão Submit */}
          <button
            type="submit"
            disabled={loading || !senhaValida || formData.senha !== formData.confirmarSenha}
            className="w-full bg-blue-600 text-white py-3 rounded-lg font-semibold hover:bg-blue-700 transition-colors disabled:opacity-50 disabled:cursor-not-allowed flex items-center justify-center gap-2"
          >
            {loading ? (
              <>
                <div className="animate-spin rounded-full h-5 w-5 border-b-2 border-white"></div>
                Criando conta...
              </>
            ) : (
              <>
                <UserPlus className="w-5 h-5" />
                Finalizar Cadastro
              </>
            )}
          </button>
        </form>

        {/* Footer */}
        <div className="mt-6 text-center">
          <p className="text-sm text-gray-600">
            Já tem uma conta?{' '}
            <button
              onClick={() => navigate('/login')}
              className="text-blue-600 hover:text-blue-700 font-medium"
            >
              Fazer login
            </button>
          </p>
        </div>
      </div>
    </div>
  );
};

export default CadastroConvite;
