import React, { useState, useEffect } from 'react';
import { Plus, Search, DollarSign, Users, AlertCircle, CheckCircle, Clock, Filter, Edit, Trash2, Eye, Download, Mail, Send, Settings } from 'lucide-react';
import emailService from './services/emailService';
import { EmailSettingsModal } from './components/EmailSettingsModal';
import ClientEmailSettings from './components/ClientEmailSettings';

// ============================================
// COMPONENTE EMAIL MANAGER (SIMPLIFICADO)
// ============================================
const EmailManager = ({ clientes }) => {
  const [selectedTemplate, setSelectedTemplate] = useState('');
  const [subject, setSubject] = useState('');
  const [selectedClients, setSelectedClients] = useState([]);
  const [loading, setLoading] = useState(false);

  const templates = [
    { value: 'primeira-cobranca', label: 'Primeira Cobran√ßa (Notifica√ß√£o Inicial)', subject: 'Notifica√ß√£o de Pend√™ncia Financeira - PROTEQ' },
    { value: 'cobranca-7dias', label: 'Cobran√ßa Leve (‚â• 7 dias de atraso)', subject: 'Lembrete Amig√°vel: Pagamento em Atraso - PROTEQ' },
    { value: 'cobranca-15dias', label: 'Cobran√ßa Moderada (‚â• 15 dias de atraso)', subject: '‚ö†Ô∏è Importante: Regulariza√ß√£o Necess√°ria - PROTEQ' },
    { value: 'cobranca-30dias', label: 'Cobran√ßa Pesada (‚â• 30 dias de atraso)', subject: 'üö® URGENTE: Notifica√ß√£o Final - PROTEQ' },
    { value: 'solicitacao-contato', label: 'Cliente entrar em contato', subject: 'Entrar em contato conosco - PROTEQ' },
  ];

  // FUN√á√ïES AUXILIARES (CENTRALIZADAS)
  const calcularStatus = (cliente) => {
    if (cliente.valorPago >= cliente.valorTotal) return 'pago';
    if (cliente.proximoVencimento && new Date(cliente.proximoVencimento) < new Date()) return 'em_atraso';
    return 'pendente';
  };

  const calcularDiasAtraso = (dataVencimento) => {
    if (!dataVencimento) return 0;
    const hoje = new Date();
    const vencimento = new Date(dataVencimento);
    const diferenca = hoje - vencimento;
    return Math.max(0, Math.ceil(diferenca / (1000 * 60 * 60 * 24)));
  };

  const calcularParcelasEmAtraso = (cliente) => {
    const status = calcularStatus(cliente);
    if (status !== 'em_atraso') return 0;

    const parcelasRestantes = cliente.parcelas - cliente.parcelasPagas;
    const diasAtraso = calcularDiasAtraso(cliente.proximoVencimento);
    const parcelasAtrasadasEstimadas = Math.min(Math.ceil(diasAtraso / 30), parcelasRestantes);

    return parcelasAtrasadasEstimadas || 1;
  };

  const formatarValorParaEmail = (valor) => {
    // Garante que o valor seja um n√∫mero
    const num = typeof valor === 'number' ? valor : parseFloat(valor) || 0;
    return num.toFixed(2).replace('.', ',');
  };

  const sendEmails = async () => {
    if (!selectedTemplate || !subject || selectedClients.length === 0) {
      alert('Por favor, preencha todos os campos e selecione pelo menos um cliente.');
      return;
    }

    setLoading(true);
    try {
      const recipients = selectedClients.map(id => {
        const cliente = clientes.find(c => c.id === id);
        const parcelasAtraso = calcularParcelasEmAtraso(cliente);
        const valorPendente = cliente.valorTotal - cliente.valorPago;

        return {
          email: cliente.email || 'cliente@exemplo.com',
          nomeResponsavel: cliente.nomeResponsavel || 'Respons√°vel',
          nomeEmpresa: cliente.nomeEmpresa || 'Empresa',
          cnpj: cliente.cnpj || 'N√£o informado',
          // CORRIGIDO: Formata√ß√£o consistente
          valorPendente: formatarValorParaEmail(valorPendente),
          parcelasAtraso: parcelasAtraso.toString(),
          proximoVencimento: cliente.proximoVencimento || 'N√£o informado',
          linkPagamento: cliente.linkPagamento || '#'
        };
      });

      console.log('üì§ Enviando emails:', {
        total: recipients.length,
        template: selectedTemplate,
        subject
      });

      const response = await emailService.sendBulkEmails({
        recipients,
        subject,
        template: selectedTemplate
      });

      if (response.success) {
        alert(`‚úÖ Emails enviados com sucesso!\n\n${response.statistics.successful} enviados\n${response.statistics.failed} falhas\nTaxa de sucesso: ${response.statistics.successRate}`);
        setSelectedClients([]);
        setSubject('');
        setSelectedTemplate('');
      }
    } catch (error) {
      console.error('‚ùå Erro ao enviar emails:', error);
      alert(`Erro ao enviar emails:\n${error.response?.data?.message || error.message}`);
    } finally {
      setLoading(false);
    }
  };

  const handleTemplateChange = (template) => {
    setSelectedTemplate(template);
    const templateData = templates.find(t => t.value === template);
    if (templateData && !subject) {
      setSubject(templateData.subject);
    }
  };

  const handleClientToggle = (clientId) => {
    if (selectedClients.includes(clientId)) {
      setSelectedClients(selectedClients.filter(id => id !== clientId));
    } else {
      setSelectedClients([...selectedClients, clientId]);
    }
  };

  const getClientStatus = (cliente) => {
    const status = calcularStatus(cliente);
    if (status === 'pago') return { text: 'Pago', color: 'bg-green-100 text-green-800' };
    if (status === 'em_atraso') return { text: 'Em Atraso', color: 'bg-red-100 text-red-800' };
    return { text: 'Pendente', color: 'bg-yellow-100 text-yellow-800' };
  };

  return (
    <div className="bg-white p-6 rounded-lg shadow-md mb-6">
      <h3 className="text-lg font-bold mb-4 flex items-center gap-2">
        <Mail className="w-5 h-5 text-purple-600" />
        Email Marketing
      </h3>

      <div className="grid grid-cols-1 lg:grid-cols-2 gap-6">
        {/* Formul√°rio */}
        <div className="space-y-4">
          <div>
            <label className="block text-sm font-medium text-gray-700 mb-2">Template de Email:</label>
            <select 
              className="w-full p-3 border border-gray-300 rounded-lg focus:ring-2 focus:ring-purple-500"
              value={selectedTemplate}
              onChange={(e) => handleTemplateChange(e.target.value)}
            >
              <option value="">Selecione um template</option>
              {templates.map(template => (
                <option key={template.value} value={template.value}>
                  {template.label}
                </option>
              ))}
            </select>
          </div>

          <div>
            <label className="block text-sm font-medium text-gray-700 mb-2">Assunto do Email:</label>
            <input 
              type="text"
              className="w-full p-3 border border-gray-300 rounded-lg focus:ring-2 focus:ring-purple-500"
              value={subject}
              onChange={(e) => setSubject(e.target.value)}
              placeholder="Digite o assunto do email"
            />
          </div>

          <div>
            <label className="block text-sm font-medium text-gray-700 mb-2">Selecionar Destinat√°rios:</label>
            <div className="border border-gray-300 rounded-lg p-3 max-h-40 overflow-y-auto">
              {clientes.length > 0 ? clientes.map(cliente => {
                const status = getClientStatus(cliente);
                return (
                  <label key={cliente.id} className="flex items-center gap-2 p-2 hover:bg-gray-50 rounded cursor-pointer">
                    <input
                      type="checkbox"
                      checked={selectedClients.includes(cliente.id)}
                      onChange={() => handleClientToggle(cliente.id)}
                    />
                    <span className="text-sm flex-1">
                      {cliente.nomeResponsavel} - {cliente.nomeEmpresa}
                    </span>
                    <span className={`px-2 py-1 rounded-full text-xs font-medium ${status.color}`}>
                      {status.text}
                    </span>
                  </label>
                );
              }) : (
                <p className="text-gray-500 text-center py-4">Nenhum cliente cadastrado</p>
              )}
            </div>
            <p className="text-sm text-gray-500 mt-2">
              {selectedClients.length} clientes selecionados
            </p>
          </div>

          <div className="flex gap-3">
            <button
              onClick={sendEmails}
              disabled={loading || !selectedTemplate || !subject || selectedClients.length === 0}
              className="flex-1 bg-purple-600 text-white px-4 py-3 rounded-lg hover:bg-purple-700 disabled:opacity-50 disabled:cursor-not-allowed flex items-center justify-center gap-2"
            >
              {loading ? (
                <div className="w-4 h-4 border-2 border-white border-t-transparent rounded-full animate-spin"></div>
              ) : (
                <Send className="w-4 h-4" />
              )}
              {loading ? 'Enviando...' : 'Enviar Emails'}
            </button>

            <button
              onClick={() => {
                setSelectedClients([]);
                setSubject('');
                setSelectedTemplate('');
              }}
              className="px-4 py-3 border border-gray-300 text-gray-700 rounded-lg hover:bg-gray-50"
            >
              Limpar
            </button>
          </div>
        </div>

        {/* Estat√≠sticas */}
        <div className="space-y-4">
          <div className="bg-gray-50 p-4 rounded-lg">
            <h4 className="font-medium text-gray-700 mb-3">Estat√≠sticas:</h4>
            <div className="grid grid-cols-2 gap-4 text-sm">
              <div>
                <span className="text-gray-600">Total de clientes:</span>
                <span className="font-bold text-blue-600 ml-2">{clientes.length}</span>
              </div>
              <div>
                <span className="text-gray-600">Selecionados:</span>
                <span className="font-bold text-purple-600 ml-2">{selectedClients.length}</span>
              </div>
              <div>
                <span className="text-gray-600">Em atraso:</span>
                <span className="font-bold text-red-600 ml-2">
                  {clientes.filter(c => calcularStatus(c) === 'em_atraso').length}
                </span>
              </div>
              <div>
                <span className="text-gray-600">Pendentes:</span>
                <span className="font-bold text-yellow-600 ml-2">
                  {clientes.filter(c => c.valorPago < c.valorTotal).length}
                </span>
              </div>
            </div>
          </div>

          <div className="bg-blue-50 p-4 rounded-lg">
            <h4 className="font-medium text-blue-800 mb-2">Como usar:</h4>
            <ul className="text-sm text-blue-700 space-y-1">
              <li>‚Ä¢ Escolha um template apropriado</li>
              <li>‚Ä¢ Personalize o assunto se necess√°rio</li>
              <li>‚Ä¢ Selecione os clientes desejados</li>
              <li>‚Ä¢ Clique em "Enviar Emails"</li>
            </ul>
          </div>

          {selectedTemplate && (
            <div className="bg-green-50 p-4 rounded-lg">
              <h4 className="font-medium text-green-800 mb-2">Template Selecionado:</h4>
              <p className="text-sm text-green-700">
                {templates.find(t => t.value === selectedTemplate)?.label}
              </p>
            </div>
          )}
        </div>
      </div>
    </div>
  );
};

