import axios from 'axios';

const API_BASE_URL = 'http://localhost:5000/api';

// Configurar axios com limites maiores
const axiosInstance = axios.create({
  baseURL: API_BASE_URL,
  timeout: 60000, // 60 segundos
  maxContentLength: 50 * 1024 * 1024, // 50MB
  maxBodyLength: 50 * 1024 * 1024, // 50MB
  headers: {
    'Content-Type': 'application/json',
  }
});

const emailService = {
  /**
   * Enviar emails em massa
   * @param {Object} emailData - { recipients, subject, template, variables }
   */
  sendBulkEmails: async (emailData) => {
    try {
      // Valida√ß√£o b√°sica
      if (!emailData.recipients || emailData.recipients.length === 0) {
        throw new Error('Nenhum destinat√°rio fornecido');
      }

      if (!emailData.subject || !emailData.template) {
        throw new Error('Assunto e template s√£o obrigat√≥rios');
      }

      // OTIMIZA√á√ÉO: Se tiver muitos destinat√°rios, dividir em lotes
      const BATCH_SIZE = 50; // M√°ximo 50 emails por request
      
      if (emailData.recipients.length > BATCH_SIZE) {
        console.log(`üì¶ Dividindo ${emailData.recipients.length} emails em lotes de ${BATCH_SIZE}`);
        return await sendInBatches(emailData, BATCH_SIZE);
      }

      // Envio normal (menos de 50 emails)
      console.log('üì§ Enviando emails...', {
        total: emailData.recipients.length,
        template: emailData.template
      });

      const response = await axiosInstance.post('/email/send-bulk', emailData);
      
      console.log('‚úÖ Resposta recebida:', response.data);
      
      return response.data;

    } catch (error) {
      console.error('‚ùå Erro no emailService:', error);
      
      // Tratamento de erros espec√≠ficos
      if (error.response) {
        // Servidor respondeu com erro
        const status = error.response.status;
        const message = error.response.data?.message || error.message;
        
        if (status === 431) {
          throw new Error('Dados muito grandes. Tente enviar menos emails por vez (m√°x: 50)');
        }
        
        if (status === 429) {
          throw new Error('Limite de emails excedido. Aguarde um momento e tente novamente.');
        }
        
        if (status === 413) {
          throw new Error('Payload muito grande. Reduza a quantidade de emails.');
        }
        
        throw new Error(message);
      } else if (error.request) {
        // Request foi feito mas n√£o houve resposta
        throw new Error('Servidor n√£o respondeu. Verifique se o backend est√° rodando.');
      } else {
        // Erro ao configurar o request
        throw new Error(error.message);
      }
    }
  },

  /**
   * Enviar email √∫nico
   */
  sendSingleEmail: async (emailData) => {
    try {
      const response = await axiosInstance.post('/email/send', emailData);
      return response.data;
    } catch (error) {
      console.error('Erro ao enviar email √∫nico:', error);
      throw error;
    }
  },

  /**
   * Obter templates dispon√≠veis
   */
  getTemplates: async () => {
    try {
      const response = await axiosInstance.get('/email/templates');
      return response.data;
    } catch (error) {
      console.error('Erro ao obter templates:', error);
      throw error;
    }
  },

  /**
   * Preview de template
   */
  previewTemplate: async (template, variables) => {
    try {
      const response = await axiosInstance.post('/email/preview', {
        template,
        variables
      });
      return response.data;
    } catch (error) {
      console.error('Erro ao gerar preview:', error);
      throw error;
    }
  },

  /**
   * Obter estat√≠sticas de envio
   */
  getStatistics: async () => {
    try {
      const response = await axiosInstance.get('/email/statistics');
      return response.data;
    } catch (error) {
      console.error('Erro ao obter estat√≠sticas:', error);
      throw error;
    }
  }
};

/**
 * FUN√á√ÉO AUXILIAR: Enviar em lotes
 * Divide grandes quantidades de emails em lotes menores
 */
async function sendInBatches(emailData, batchSize) {
  const recipients = emailData.recipients;
  const totalBatches = Math.ceil(recipients.length / batchSize);
  
  console.log(`üì¶ Enviando ${recipients.length} emails em ${totalBatches} lotes`);
  
  const results = {
    success: true,
    message: 'Envio em lotes conclu√≠do',
    statistics: {
      total: recipients.length,
      successful: 0,
      failed: 0,
      successRate: '0%'
    },
    results: []
  };

  for (let i = 0; i < totalBatches; i++) {
    const start = i * batchSize;
    const end = Math.min(start + batchSize, recipients.length);
    const batch = recipients.slice(start, end);
    
    console.log(`üì® Enviando lote ${i + 1}/${totalBatches} (${batch.length} emails)`);
    
    try {
      const batchData = {
        ...emailData,
        recipients: batch
      };
      
      const response = await axiosInstance.post('/email/send-bulk', batchData);
      
      if (response.data.statistics) {
        results.statistics.successful += response.data.statistics.successful;
        results.statistics.failed += response.data.statistics.failed;
      }
      
      if (response.data.results) {
        results.results.push(...response.data.results);
      }
      
      console.log(`‚úÖ Lote ${i + 1} conclu√≠do`);
      
      // Delay entre lotes para n√£o sobrecarregar
      if (i < totalBatches - 1) {
        await new Promise(resolve => setTimeout(resolve, 2000)); // 2 segundos
      }
      
    } catch (error) {
      console.error(`‚ùå Erro no lote ${i + 1}:`, error);
      results.statistics.failed += batch.length;
      results.success = false;
    }
  }
  
  // Calcular taxa de sucesso
  results.statistics.successRate = 
    ((results.statistics.successful / results.statistics.total) * 100).toFixed(2) + '%';
  
  console.log('‚úÖ Envio em lotes finalizado:', results.statistics);
  
  return results;
}

export default emailService;
