import React, { useState, useEffect } from 'react';
import { Plus, Search, DollarSign, Users, AlertCircle, CheckCircle, Clock, Mail, Send, Settings } from 'lucide-react';
import emailService from './services/emailService';

// FUNÇÕES AUXILIARES
const calcularStatus = (cliente) => {
  if (cliente.valorPago >= cliente.valorTotal) return 'pago';
  if (cliente.proximoVencimento && new Date(cliente.proximoVencimento) < new Date()) return 'em_atraso';
  return 'pendente';
};

const formatarMoeda = (valor) => {
  return new Intl.NumberFormat('pt-BR', { style: 'currency', currency: 'BRL' }).format(valor);
};

// COMPONENTE EMAIL MANAGER
const EmailManager = ({ clientes }) => {
  const [selectedTemplate, setSelectedTemplate] = useState('');
  const [subject, setSubject] = useState('');
  const [selectedClients, setSelectedClients] = useState([]);
  const [loading, setLoading] = useState(false);

  const templates = [
    { value: 'primeira-cobranca', label: 'Primeira Cobrança', subject: 'Notificação - PROTEQ' }
  ];

  const sendEmails = async () => {
    if (!selectedTemplate || !subject || selectedClients.length === 0) {
      alert('Preencha todos os campos');
      return;
    }

    setLoading(true);
    try {
      const recipients = selectedClients.map(id => {
        const c = clientes.find(cliente => cliente.id === id);
        return {
          email: c.email,
          nomeResponsavel: c.nomeResponsavel,
          nomeEmpresa: c.nomeEmpresa,
          cnpj: c.cnpj || 'N/A',
          valorPendente: (c.valorTotal - c.valorPago).toFixed(2),
          parcelasAtraso: '1',
          proximoVencimento: c.proximoVencimento || 'N/A',
          linkPagamento: c.linkPagamento || '#'
        };
      });

      const response = await emailService.sendBulkEmails({ recipients, subject, template: selectedTemplate });

      if (response.success) {
        alert('Emails enviados!');
        setSelectedClients([]);
      }
    } catch (error) {
      alert('Erro: ' + error.message);
    } finally {
      setLoading(false);
    }
  };

  return (
    <div className="bg-white p-6 rounded-lg shadow-md mb-6">
      <h3 className="text-lg font-bold mb-4 flex items-center gap-2">
        <Mail className="w-5 h-5 text-purple-600" />
        Email Marketing
      </h3>

      <div className="space-y-4">
        <select 
          className="w-full p-3 border rounded-lg"
          value={selectedTemplate}
          onChange={(e) => setSelectedTemplate(e.target.value)}
        >
          <option value="">Selecione template</option>
          {templates.map(t => <option key={t.value} value={t.value}>{t.label}</option>)}
        </select>

        <input 
          type="text"
          className="w-full p-3 border rounded-lg"
          value={subject}
          onChange={(e) => setSubject(e.target.value)}
          placeholder="Assunto"
        />

        <div className="border rounded-lg p-3 max-h-40 overflow-y-auto">
          {clientes.map(c => (
            <label key={c.id} className="flex items-center gap-2 p-2">
              <input
                type="checkbox"
                checked={selectedClients.includes(c.id)}
                onChange={() => {
                  if (selectedClients.includes(c.id)) {
                    setSelectedClients(selectedClients.filter(id => id !== c.id));
                  } else {
                    setSelectedClients([...selectedClients, c.id]);
                  }
                }}
              />
              <span className="text-sm">{c.nomeResponsavel} - {c.nomeEmpresa}</span>
            </label>
          ))}
        </div>

        <button
          onClick={sendEmails}
          disabled={loading}
          className="w-full bg-purple-600 text-white px-4 py-3 rounded-lg hover:bg-purple-700 disabled:opacity-50"
        >
          {loading ? 'Enviando...' : 'Enviar Emails'}
        </button>
      </div>
    </div>
  );
};

// COMPONENTE PRINCIPAL
const FinancialManager = () => {
  const [clientes, setClientes] = useState([]);
  const [filtros, setFiltros] = useState({ status: 'todos', busca: '' });

  useEffect(() => {
    const dados = localStorage.getItem('financial-manager-clientes');
    if (dados) {
      try {
        setClientes(JSON.parse(dados));
      } catch (e) {
        console.error(e);
      }
    }
  }, []);

  useEffect(() => {
    if (clientes.length > 0) {
      localStorage.setItem('financial-manager-clientes', JSON.stringify(clientes));
    }
  }, [clientes]);

  const totais = {
    totalAReceber: clientes.reduce((acc, c) => acc + (c.valorTotal - c.valorPago), 0),
    totalRecebido: clientes.reduce((acc, c) => acc + c.valorPago, 0),
    totalClientes: clientes.length
  };

  return (
    <div className="min-h-screen bg-gray-50 p-6">
      <div className="max-w-7xl mx-auto">
        <h1 className="text-3xl font-bold mb-8">Gerenciamento Financeiro</h1>

        {/* Cards Resumo */}
        <div className="grid grid-cols-1 md:grid-cols-3 gap-6 mb-8">
          <div className="bg-white p-6 rounded-lg shadow-md">
            <p className="text-sm text-gray-600">Total a Receber</p>
            <p className="text-2xl font-bold text-blue-600">{formatarMoeda(totais.totalAReceber)}</p>
          </div>
          <div className="bg-white p-6 rounded-lg shadow-md">
            <p className="text-sm text-gray-600">Total Recebido</p>
            <p className="text-2xl font-bold text-green-600">{formatarMoeda(totais.totalRecebido)}</p>
          </div>
          <div className="bg-white p-6 rounded-lg shadow-md">
            <p className="text-sm text-gray-600">Total Clientes</p>
            <p className="text-2xl font-bold text-gray-700">{totais.totalClientes}</p>
          </div>
        </div>

        {/* Email Marketing */}
        <EmailManager clientes={clientes} />

        {/* Lista de Clientes */}
        <div className="bg-white rounded-lg shadow-md p-6">
          <h2 className="text-xl font-bold mb-4">Clientes</h2>

          {clientes.length === 0 ? (
            <p className="text-center text-gray-500 py-8">Nenhum cliente cadastrado</p>
          ) : (
            <div className="space-y-4">
              {clientes.map(c => (
                <div key={c.id} className="border rounded-lg p-4 hover:bg-gray-50">
                  <div className="flex justify-between items-start">
                    <div>
                      <h3 className="font-bold">{c.nomeResponsavel}</h3>
                      <p className="text-sm text-gray-600">{c.nomeEmpresa}</p>
                      <p className="text-sm text-gray-600">{c.email}</p>
                    </div>
                    <div className="text-right">
                      <p className="font-bold">{formatarMoeda(c.valorTotal)}</p>
                      <p className="text-sm text-green-600">Pago: {formatarMoeda(c.valorPago)}</p>
                      <span className={`inline-block px-2 py-1 rounded text-xs ${
                        calcularStatus(c) === 'pago' ? 'bg-green-100 text-green-800' :
                        calcularStatus(c) === 'em_atraso' ? 'bg-red-100 text-red-800' :
                        'bg-yellow-100 text-yellow-800'
                      }`}>
                        {calcularStatus(c) === 'pago' ? 'Pago' :
                         calcularStatus(c) === 'em_atraso' ? 'Atrasado' : 'Pendente'}
                      </span>
                    </div>
                  </div>
                </div>
              ))}
            </div>
          )}
        </div>
      </div>
    </div>
  );
};

export default FinancialManager;
