      'Valor Pago': `R$ ${cliente.valorPago.toFixed(2).replace('.', ',')}`,
      'Valor Restante': `R$ ${(cliente.valorTotal - cliente.valorPago).toFixed(2).replace('.', ',')}`,
      'Parcelas': `${cliente.parcelasPagas}/${cliente.parcelas}`,
